import React, {Component} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';

import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    UncontrolledDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem } from 'reactstrap';
import {Link} from 'react-router-dom';

export class RepresentativeInstance extends Component {
    constructor(props) {
      super(props);
      this.state = {
        bio:"",
      };
    }

    render() {
        var image;
     console.log(this.props.name)
      if(this.props.name == "H.R. 3079 Energy Savings Through Public-Private Partnerships Act of 2019"){
        image = "3079";
        this.state.bio = "To amend the National Energy Conservation Policy Act to encourage the increased use of performance contracting in Federal facilities, and for other purposes. Sponsor is Peter Welch Representative for Vermont At Large. Democrat."
      }
      else if(this.props.name == "H.R. 2960 Energy and Water Development and Related Agencies Appropriations"){
        image = "2960";
        this.state.bio = "Making appropriations for energy and water development and related agencies for the fiscal year ending September 30, 2020, and for other purposes.Sponsor is Marcy Kaptur representative for Ohio's 9th congressional district. Democrat."
      }
      else{
        image = "2746";
        this.state.bio = "This bill amends the Public Utility Regulatory Policies Act of 1978 to require electric utilities to obtain a minimum percentage of the electricity they sell in 2019 through 2042 from renewable resources. The required annual percentage increases from 10% in 2019 to 35% for 2032 through 2042. Sponsor is Peter Welch Representative for Vermont At Large. Democrat."
      }
      return (
        <div center className="RepresentativeInstance"style = {{paddingLeft:'3%',textAlign: 'center',color: '#000000'}}>
            <img style={{paddingTop:'4%',width: '260px', height: '375px'}} src={require('./'+image+'.jpg')}/>
            <p style = {{paddingTop: "3%",color: '#000000'}}> {this.props.name} </p>
            <p style = {{color: '#000000'}}> {this.state.bio} </p>
            <p style = {{color: '#000000'}}> Sponsor/Cosponsor:{this.props.sponsor} </p>
            <p style = {{color: '#000000'}}> Year Introduced:{this.props.year} </p>
            <p style = {{color: '#000000'}}> Margin it was passed/defeated by:{this.props.result} </p>
            <p style = {{color: '#000000'}}> Partisanship of sponsor/cosponsors:{this.props.party} </p>
            <p style = {{color: '#000000'}}> Chamber of Origin:{this.props.chamber} </p>
        </div>
      );
    }
}

export default EnergyBillsInstance;
