import React, {Component} from "react";
import './App.css';
import Navigation from './Navbar.js';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Route, BrowserRouter} from 'react-router-dom';
import {Representatives} from './Representatives.js';
import {About} from './About.js';
import {Home} from './Home.js';
import {States} from './States.js';
import {EnergyBills} from './EnergyBills.js';
import {RepresentativeInstance} from './RepresentativeInstance.js';
import {EnergyBillsInstance} from './EnergyBillsInstance.js';
import {StatesInstance} from './StatesInstance.js';




// import { Navbar, Nav, NavItem, NavDropdown, MenuItem, Button, Form, FormControl} from 'react-bootstrap';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
        tedcruz: "tedcruz",
        jeffmerkley: "jeffmerkley",
        aocname: "aoc",
        data: {0: ["Ted Cruz", "Texas", "Republican", "2013-present", "Senate"], 1: ["Jeff Merkley","Oregon", "Democrat", "2009-present", "Senate"], 2: ["Alexandria Ocasio-Cortez","New York", "Democrat", "2019-present", "House"]} ,
        bills: {0: ["H.R. 3079 Energy Savings Through Public-Private Partnerships Act of 2019", " Peter Welch, Adam Kinzinger", "2019", "N/A", "1 Democrat, 1 Republican", "House"], 1: ["H.R. 2960 Energy and Water Development and Related Agencies Appropriations Act, 2020","Marcy Kaptur","2019", "N/A", "1 Democrat", "House"], 2: [" H.R.2746 - American Renewable Energy and Efficiency Act","Peter Welch, Ben Lujan, Barbara Lee, Yvette Clarke, Ro Khanna, Mike Quigley, Mark Pocan, Ted Lieu, Matt Cartwright, Alan Lowenthal, Joseph Crowley","2017", "N/A", "11 Democrat", "House"]},
        states: {0: ["California","39,560,000", "2.751 trillion", "West Coast", "Democrat"], 1:["New York", "8,623,000", "1.5 trillion", "Northeast", "Democrat"], 2:["Texas", "28,700,000", "1.645 trillion", "Southwest", "Republican"]}
      };
    }
  render(){
    return (
      <div className="App">
        <Navigation/>
        <BrowserRouter>
          <Route exact path="/" component={Home}  />
          <Route path="/representatives" component={Representatives}  />
          <Route path="/about" component={About}  />
          <Route path="/home" component={Home}  />
          <Route path="/energy-bills" component={EnergyBills}  />
          <Route path="/states" component={States}  />
          <Route path="/name" component={RepresentativeInstance} />
          <Route path="/john" render={(props) => <RepresentativeInstance {...props} name="john" />} />
          <Route path={"/politician/" + this.state.tedcruz} render={(props) => <RepresentativeInstance {...props} name={this.state.data["0"][0]} state={this.state.data["0"][1]} party={this.state.data["0"][2]} date={this.state.data["0"][3]} chamber={this.state.data["0"][4]} />} />
          <Route path={"/politician/" + this.state.jeffmerkley} render={(props) => <RepresentativeInstance {...props} name={this.state.data["1"][0]} state={this.state.data["1"][1]} party={this.state.data["1"][2]} date={this.state.data["1"][3]} chamber={this.state.data["1"][4]}/>} />
          <Route path={"/politician/" + this.state.aocname} render={(props) => <RepresentativeInstance {...props} name={this.state.data["2"][0]} state={this.state.data["2"][1]} party={this.state.data["2"][2]} date={this.state.data["2"][3]} chamber={this.state.data["2"][4]}/>} />
          <Route path={"/bill/3079"} render={(props) => <EnergyBillsInstance {...props} name={this.state.bills["0"][0]} sponsor={this.state.bills["0"][1]} year={this.state.bills["0"][2]} result={this.state.bills["0"][3]} party={this.state.bills["0"][4]} chamber={this.state.bills["0"][5]} />} />
          <Route path={"/bill/2960"} render={(props) => <EnergyBillsInstance {...props} name={this.state.bills["1"][0]} sponsor={this.state.bills["1"][1]} year={this.state.bills["1"][2]} result={this.state.bills["1"][3]} party={this.state.bills["1"][4]} chamber={this.state.bills["1"][5]}/>} />
          <Route path={"/bill/2746"} render={(props) => <EnergyBillsInstance {...props} name={this.state.bills["2"][0]} sponsor={this.state.bills["2"][1]} year={this.state.bills["2"][2]} result={this.state.bills["2"][3]} party={this.state.bills["2"][4]} chamber={this.state.bills["2"][5]}/>} />
          <Route path={"/state/california"} render={(props) => <StateInstance {...props} name={this.state.states["0"][0]} population={this.state.states["0"][1]} gdp={this.state.states["0"][2]} region={this.state.states["0"][3]} party={this.state.states["0"][4]} />} />
          <Route path={"/state/newyork"} render={(props) => <StateInstance {...props} name={this.state.states["1"][0]} population={this.state.states["1"][1]} gdp={this.state.states["1"][2]} region={this.state.states["1"][3]} party={this.state.states["1"][4]}/>} />
          <Route path={"/state/texas"} render={(props) => <StateInstance {...props} name={this.state.states["2"][0]} population={this.state.states["2"][1]} gdp={this.state.states["2"][2]} region={this.state.states["2"][3]} party={this.state.states["2"][4]}/>} />
        </BrowserRouter>                        
      
        
      </div>
    );
  }
}
  

export default App;
