import React, {Component} from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Col,
    Card, CardBody,
    CardTitle, CardSubtitle, Button, CardDeck,
     } from 'reactstrap';
import { BrowserRouter} from 'react-router-dom';
export class Representatives extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            isLoading: true,
        }
        
        
    }
    componentDidMount() {
        this.setState({isLoading: false});
        fetch('http://3.84.54.129/api/congressmembers')
        .then(res => res.json())
        .then((data) => {
            this.setState({ items: data,isLoading: true});      
        })
        .catch(console.log)
    }
    
    render() {

        this.state.isLoading = this.state;
        if (!this.state.isLoading){
            return false;
        }
        
        console.log(this.state.items)        
        
      return (
        
        <div className="Representatives" style={{width:"100%", height:"60%"}}>
        
           <div>
            <Col sm={{ size: 'auto'}}>
                {/* <CardImg top width="80%" src={john} alt="Card image cap" /> */}

                <CardDeck>
                    {this.state.items.map(item =>
                        <Card>
                            <CardBody>
                            <CardTitle>{item.people.objects.first_name}{item.people.objects.last_name}</CardTitle>
                            <CardSubtitle>State: {item.people.objects.state}</CardSubtitle>
                            <CardSubtitle>Party: {item.people.objects.party}</CardSubtitle>
                            <CardSubtitle>Years Active: 2002-present</CardSubtitle>
                            <CardSubtitle>Title:  {item.people.objects.title}</CardSubtitle>
                            <BrowserRouter>
                                <Button href= {"/politician/" + item.people.objects.member_id} class="nav-link"  to={"/politician/" + item.people.objects.member_id}> See More</Button>
                            </BrowserRouter>
                            </CardBody>
                        </Card>
                    )}
                    </CardDeck>
                </Col>
               
            </div>
           
        </div>
    
      );
    }
}

export default Representatives;
