import React, {Component} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';

import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem, ListGroup, ListGroupItem, ListGroupItemHeading, ListGroupItemText,
    NavLink,
    UncontrolledDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem } from 'reactstrap';
import {Link} from 'react-router-dom';

export class EnergyBillsInstance extends Component {
    constructor(props) {
      super(props);
      this.state = {
        bio:"",
      };
    }

    render() {
        var image;
        var sponsor;
        var link;
     console.log(this.props.name)
      if(this.props.name == "H.R. 3079 Energy Savings Through Public-Private Partnerships Act of 2019"){
        image = "peterWelch";
        sponsor = "/politicians/peterwelch"
        link = "https://www.congress.gov/116/bills/hr3079/BILLS-116hr3079ih.pdf";
        this.state.bio = "To amend the National Energy Conservation Policy Act to encourage the increased use of performance contracting in Federal facilities, and for other purposes."
      }
      else if(this.props.name == "H.R. 2960 Energy and Water Development and Related Agencies Appropriations Act"){
        link = "https://www.congress.gov/116/bills/hr2960/BILLS-116hr2960rh.pdf";
        image = "marcyKaptur";
        sponsor = "";
        this.state.bio = "Making appropriations for energy and water development and related agencies for the fiscal year ending September 30, 2020, and for other purposes."
      }
      else{
        image = "peterWelch";
        link = "https://www.congress.gov/115/bills/hr2746/BILLS-115hr2746ih.pdf";
        sponsor = "/politicians/peterwelch"
        this.state.bio = "This bill amends the Public Utility Regulatory Policies Act of 1978 to require electric utilities to obtain a minimum percentage of the electricity they sell in 2019 through 2042 from renewable resources. The required annual percentage increases from 10% in 2019 to 35% for 2032 through 2042."
      }
      return (
        <div center className="RepresentativeInstance"style = {{paddingLeft:'3%',color: '#000000'}}>
            {/* <img style={{paddingTop:'4%',width: '260px', height: '375px'}} src={require('./'+image+'.jpg')}/> */}
            <center><h2 style = {{paddingTop: "3%",color: '#000000', fontSize:'25px', textAlign:'center'}}> {this.props.name} </h2></center>
            <center>
            <ListGroup style = {{paddingTop:'3%', paddingLeft:'3%',textAlign: 'center',color: '#000000', width: '50%'}}>
            <ListGroupItem>
                <ListGroupItemHeading>Summary</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.state.bio}
                    </ListGroupItemText>
                    <ListGroupItemText>
                    <a href={link}>Read Full Document</a>
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Sponsor/Cosponsor</ListGroupItemHeading>
                    <ListGroupItemText>
                    <img style={{paddingRight:'2%',width: '90px', height: '100px'}} src={require('./'+image+'.jpeg')}/>
                    </ListGroupItemText>
                    <ListGroupItemText>
                    <a href={sponsor}>{this.props.sponsor}</a>
                    </ListGroupItemText>
                    </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Year Introduced</ListGroupItemHeading>
                <ListGroupItemText>
                {this.props.year}
                </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Margin it was passed/defeated by</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.props.result}
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Partisanship of sponsor/cosponsors</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.props.party}
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Partisanship of sponsor/cosponsors</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.props.party}
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Chamber of Origin</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.props.chamber}
                    </ListGroupItemText>
                </ListGroupItem>
        </ListGroup>
        </center>
        </div>
      );
    }
}

export default EnergyBillsInstance;
