import React, {Component} from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Col,
    Card, CardBody,
    CardTitle, CardSubtitle, Button, CardDeck,
     } from 'reactstrap';
import { BrowserRouter} from 'react-router-dom';
export class Representatives extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            isLoading: false,
        }
        
        
    }
    componentDidMount() {
        this.setState({isLoading: true});
        fetch('http://3.84.54.129/api/congressmembers')
        .then(res => res.json())
        .then((data) => {
            this.setState({ items: data.objects,isLoading: false});      
        })
        .catch(console.log)
    }
    
    render() {

        const {items, isLoading} = this.state;
        console.log(isLoading)
        if (isLoading){
            console.log(isLoading)
            return false;
        }
        
        console.log(items)        
        
      return (
        
        <div className="Representatives" style={{width:"100%", height:"60%"}}>
        
           <div>
            <Col sm={{ size: 'auto'}}>
                {/* <CardImg top width="80%" src={john} alt="Card image cap" /> */}

                <CardDeck>
                    {items.map(item =>
                        <Card>
                            <CardBody>
                            <CardTitle>{item.first_name}{item.last_name}</CardTitle>
                            <CardSubtitle>State: {item.state}</CardSubtitle>
                            <CardSubtitle>Party: {item.party}</CardSubtitle>
                            <CardSubtitle>Title:  {item.title}</CardSubtitle>
                            <BrowserRouter>
                                <Button href= {"/politician/" + item.member_id} class="nav-link"  to={"/politician/" + item.member_id}> See More</Button>
                                <Route path={"/politician/" + this.state.marcykaptur} render={(props) => <RepresentativeInstance {...props}  />} />
                            </BrowserRouter>
                            </CardBody>
                        </Card>
                       
                    )}
                    </CardDeck>
                </Col>
               
            </div>

            <BrowserRouter>
         
          <Route path={"/politician/" + this.state.marcykaptur} render={(props) => <RepresentativeInstance {...props} name={this.state.data["0"][0]} state={this.state.data["0"][1]} party={this.state.data["0"][2]} date={this.state.data["0"][3]} chamber={this.state.data["0"][4]} />} />
          <Route path={"/politician/" + this.state.peterwelch} render={(props) => <RepresentativeInstance {...props} name={this.state.data["1"][0]} state={this.state.data["1"][1]} party={this.state.data["1"][2]} date={this.state.data["1"][3]} chamber={this.state.data["1"][4]}/>} />
          <Route path={"/politician/" + this.state.johncornyn} render={(props) => <RepresentativeInstance {...props} name={this.state.data["2"][0]} state={this.state.data["2"][1]} party={this.state.data["2"][2]} date={this.state.data["2"][3]} chamber={this.state.data["2"][4]}/>} />
          <Route path={"/bill/1685"} render={(props) => <EnergyBillsInstance {...props} name={this.state.bills["0"][0]} sponsor={this.state.bills["0"][1]} year={this.state.bills["0"][2]} result={this.state.bills["0"][3]} party={this.state.bills["0"][4]} chamber={this.state.bills["0"][5]} />} />
          <Route path={"/bill/2960"} render={(props) => <EnergyBillsInstance {...props} name={this.state.bills["1"][0]} sponsor={this.state.bills["1"][1]} year={this.state.bills["1"][2]} result={this.state.bills["1"][3]} party={this.state.bills["1"][4]} chamber={this.state.bills["1"][5]}/>} />
          <Route path={"/bill/2746"} render={(props) => <EnergyBillsInstance {...props} name={this.state.bills["2"][0]} sponsor={this.state.bills["2"][1]} year={this.state.bills["2"][2]} result={this.state.bills["2"][3]} party={this.state.bills["2"][4]} chamber={this.state.bills["2"][5]}/>} />
          <Route path={"/state/ohio"} render={(props) => <StatesInstance {...props} name={this.state.states["0"][0]} population={this.state.states["0"][1]} gdp={this.state.states["0"][2]} region={this.state.states["0"][3]} party={this.state.states["0"][4]} />} />
          <Route path={"/state/vermont"} render={(props) => <StatesInstance {...props} name={this.state.states["1"][0]} population={this.state.states["1"][1]} gdp={this.state.states["1"][2]} region={this.state.states["1"][3]} party={this.state.states["1"][4]}/>} />
          <Route path={"/state/texas"} render={(props) => <StatesInstance {...props} name={this.state.states["2"][0]} population={this.state.states["2"][1]} gdp={this.state.states["2"][2]} region={this.state.states["2"][3]} party={this.state.states["2"][4]}/>} />
        </BrowserRouter>   
           
        </div>
    
      );
    }
}

export default Representatives;
