import React, {Component} from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {RepresentativeInstance} from './RepresentativeInstance.js';
import {
    Col,
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button, CardDeck,
     } from 'reactstrap';
import {Link, BrowserRouter, Route} from 'react-router-dom';
export class Representatives extends Component {
    constructor(props) {
        super(props);
        this.state = {
            johncornyn: "johncornyn",
            peterwelch: "peterwelch",
            marcykaptur: "marcykaptur"
          };
        }

    componentDidMount() {
        fetch('http://127.0.0.1:5000/api/congressmembers')
        .then(res => res.json())
        .then((data) => {
            console.log(data);
            this.setState({ people: data });      
        })
        .catch(console.log)
      
    }
    render() {
        var person;
        for(person of this.state.people){

        }
      return (
        
        <div className="Representatives" style={{width:"100%", height:"60%"}}>
        
           <div>
            <Col sm={{ size: 'auto'}}>

                <CardDeck>
                    <Card>
                        {/* <CardImg top width="80%" src={john} alt="Card image cap" /> */}
                        <CardBody>
                        <CardTitle>John Cornyn</CardTitle>
                        <CardSubtitle>State: Texas</CardSubtitle>
                        <CardSubtitle>Party: Republican</CardSubtitle>
                        <CardSubtitle>Years Active: 2002-present</CardSubtitle>
                        <CardSubtitle>Chamber: Senate</CardSubtitle>
                        <BrowserRouter>
                            <Button href= {"/politician/" + this.state.johncornyn} class="nav-link"  to={"/politician/" + this.state.johncornyn}> See More</Button>
                        </BrowserRouter>
                        </CardBody>
                    </Card>
                    <Card>
                        {/* <CardImg top width="80%" src={peter} alt="Card image cap" /> */}
                        <CardBody>
                        <CardTitle>Peter Welch</CardTitle>
                        <CardSubtitle>State: Vermont</CardSubtitle>
                        <CardSubtitle>Party: Democrat</CardSubtitle>
                        <CardSubtitle>Years Active: 2007-present</CardSubtitle>
                        <CardSubtitle>Chamber: House of Representatives</CardSubtitle>
                        <BrowserRouter>
                            <Button href= {"/politician/" + this.state.peterwelch} class="nav-link"  to={"/politician/" + this.state.peterwelch}> See More</Button>
                        </BrowserRouter>
                        </CardBody>
                    </Card>
                    <Card>
                        {/* <CardImg top width="80%" src={marcy} alt="Card image cap" /> */}
                        <CardBody>
                        <CardTitle>Marcy Kaptur</CardTitle>
                        <CardSubtitle>State: Ohio</CardSubtitle>
                        <CardSubtitle>Party: Democrat</CardSubtitle>
                        <CardSubtitle>Years Active: 1983-present</CardSubtitle>
                        <CardSubtitle>Chamber: House</CardSubtitle>
                        <BrowserRouter>
                            <Button href= {"/politician/" + this.state.marcykaptur} class="nav-link"  to={"/politician/" + this.state.marcykaptur}> See More</Button>
                        </BrowserRouter>
                        </CardBody>
                    </Card>
                    </CardDeck>
                </Col>
               
            </div>
           
        </div>
    
      );
    }
}

export default Representatives;
