import React, {Component} from "react";
import './Navbar.css';
import Highlighter from "react-highlight-words";
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Table,Col, Pagination, PaginationItem, PaginationLink, Form, FormGroup, Label, Input, Button
     } from 'reactstrap';
export class Search extends Component {
    constructor(props) {
        super(props);
        this.state = {
            politicians : [],
            states : [],
            bills : []

        }
    }

    componentDidMount(){
        this.loadData();
    }

    loadData(){
        var total = 300;
        for(var i = 0; i < total; i++){
            fetch('https://api.empoweringknowledge.me/api/bills'+ "?page=" + i)
            .then(res => res.json())
            .then((data) => {
                total = data.total_pages
                this.setState({ bills: data.objects});      
            })
            .catch(console.log)
        }
        for(var i = 0; i < total; i++){
            fetch('https://api.empoweringknowledge.me/api/congressmembers'+ "?page=" + i)
            .then(res => res.json())
            .then((data) => {
                total = data.total_pages
                this.setState({ politicians: data.objects});      
            })
            .catch(console.log)
        }
        for(var i = 0; i < total; i++){
            fetch('https://api.empoweringknowledge.me/api/states'+ "?page=" + i)
            .then(res => res.json())
            .then((data) => {
                total = data.total_pages
                this.setState({ states: data.objects});      
            })
            .catch(console.log)
        }
    }

    getBillResults=  () => {
        let searchTerms = [...new Set(this.props.name.toLowerCase().trim().split(" "))];
        var billData;
        searchTerms.map(query => 
            billData.add(this.state.bills.filter((bill) => {
                return bill.committees.toLowerCase().includes(query)
                || bill.introduced_date.toLowerCase().includes(query) || bill.senate_passage.toLowerCase().includes(query) ||
                bill.title.toLowerCase().includes(query) || bill.sponsor_id.toLowerCase().includes(query)
            }))
        )
        var array = [];
        for (var x = 0; x < billData.length; x++) {
            array.push(
                <tr>
                    <th scope="row"></th>
                    <td> {billData[x].title} <Highlighter
                    highlightClassName="HighlightClass"
                    searchWords={searchTerms}
                    autoEscape={true}
                    textToHighlight={billData[x].title}
                    />
                    </td>
                    <td> {billData[x].sponsor_id}</td>
                    <td> {billData[x].committees}</td>
                    <td> {billData[x].introduced_date}</td>
                    <td> {billData[x].house_passage}</td>
                    <td> {billData[x].senate_passage}</td>
                    <td> {billData[x].cosponsors_by_party_D}</td>
                    <td> {billData[x].cosponsors_by_party_R}</td>
                </tr>
                // <PersonCard image={people[x].image}
                //             name={people[x].name}
                //             birth_date={people[x].birth_date}
                //             birth_place={people[x].birth_place}
                //             minority_1={people[x].minority_1}
                //             minority_2={people[x].minority_2}
                //             occupation={people[x].occupation}
                //             industry={people[x].industry}
                //             filter={[query]}/>
            );
        }
        return array;

    };

    render(){
        if(this.getBillResults().length < 1 ) {
            return (
              <div>
                <h1 style={{display: 'flex', justifyContent:'center', alignItems:'center'}}> Search Results for: "{this.props.name}" </h1>
                <h3 style={{display: 'flex', justifyContent:'center', alignItems:'center', marginTop: '10%'}}>
                  There are no results for your search.
                </h3>
              </div>
            );
        }
      
        return(
            <div>
            <Nav tabs>
              <NavItem>
                <NavLink
                  className={classnames({ active: activeTab === '1' })}
                  onClick={() => { toggle('1'); }}
                >
                  Tab1
                </NavLink>
              </NavItem>
              <NavItem>
                <NavLink
                  className={classnames({ active: activeTab === '2' })}
                  onClick={() => { toggle('2'); }}
                >
                  Moar Tabs
                </NavLink>
              </NavItem>
            </Nav>
            <TabContent activeTab={activeTab}>
              <TabPane tabId="1">
                <Row>
                  <Col sm="12">
                    <h4>Tab 1 Contents</h4>
                  </Col>
                </Row>
              </TabPane>
              <TabPane tabId="2">
                <Row>
                  <Col sm="6">
                    <Card body>
                      <CardTitle>Special Title Treatment</CardTitle>
                      <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                      <Button>Go somewhere</Button>
                    </Card>
                  </Col>
                  <Col sm="6">
                    <Card body>
                      <CardTitle>Special Title Treatment</CardTitle>
                      <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                      <Button>Go somewhere</Button>
                    </Card>
                  </Col>
                </Row>
              </TabPane>
            </TabContent>
          </div>
        )
    }
}
export default Search;
