import React, {Component} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';

import {
    ListGroup, ListGroupItem, ListGroupItemHeading, ListGroupItemText
    } from 'reactstrap';

export class StatesInstance extends Component {
  constructor(props) {
    super(props);
    this.state = {
        items: [],
        isLoading: false,
        // values: [],
        
    }
    
    
}
toLowerCase(value){
    if(value != null){
      return value.toLowerCase();  
    }
}
componentDidMount() {
    var value = window.location.href.split("/")
    var path = (value[4])
    console.log('http://3.84.54.129:5000/api/states/' + path)
    this.setState({isLoading: true});
    fetch('http://3.84.54.129:5000/api/states/' + path)
    .then(res => res.json())
    .then((data) => {
        this.setState({ items: data ,isLoading: false});      
    })
    .catch(console.log)
    
    // fetch('http://3.84.54.129:5000/api/bills?q={"filters":[{"name":"sponsor_id","op":"eq","val":"'+ path + '"}]}')
    // .then(res => res.json())
    // .then((data) => {
    //     this.setState({ values: data.objects,isLoading: false});      
    // })
    // .catch(console.log)
}

render() {

    const {items, isLoading, values} = this.state;
    var stateMap = {
      "Alabama":"al",
      "Alaska":"ak",
      "Arizona":"az",
      "Arkansas":"ar",
      "California":"ca",
      "Colorado":"co",
      "Connecticut":"ct",
      "Delaware":"de",
      "Florida":"fl",
      "Georgia":"ga",
      "Hawaii":"hi",
      "Idaho":"id",
      "Illinois":"il",
      "Indiana":"in",
      "Iowa":"ia",
      "Kansas":"ks",
      "Kentucky":"ky",
      "Louisiana":"la",
      "Maine":"me",
      "Maryland":"md",
      "Massachusetts":"ma",
      "Michigan":"mi",
      "Minnesota":"mn",
      "Mississippi":"ms",
      "Missouri":"mo",
      "Montana":"mt",
      "Nebraska":"ne",
      "Nevada":"nv",
      "New Hampshire":"nh",
      "New Jersey":"nj",
      "New Mexico":"nm",
      "New York":"ny",
      "North Carolina":"nc",
      "North Dakota":"nd",
      "Ohio":"oh",
      "Oklahoma":"ok",
      "Oregon":"or",
      "Pennsylvania":"pa",
      "Rhode Island":"ri",
      "South Carolina":"sc",
      "South Dakota":"sd",
      "Tennessee":"tn",
      "Texas":"tx",
      "Utah":"ut",
      "Vermont":"vt",
      "Virginia":"va",
      "Washington":"wa",
      "West Virginia":"wv",
      "Wisconsin":"wi",
      "Wyoming":"wy",
    };

    if (isLoading){
        return false;
    }
    

    console.log(items)
    console.log(items.name)

      return (
        <div center className="StatesInstance"style = {{paddingLeft:'3%',textAlign: 'center',color: '#000000'}}>
            <img top width="80%" style={{width: '300px', height: '375px'}} src={"https://www.state-maps.org/"+ stateMap[items.name]+".gif"} alt={"No image"} />
            <h2 style = {{paddingTop: "3%",color: '#000000'}}> {items.name} </h2>
            <center>
            <ListGroup style = {{paddingTop:'1%', paddingLeft:'3%',textAlign: 'center',color: '#000000', width: '50%'}}>
            {/* <ListGroupItem>
                <ListGroupItemHeading>Summary</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.state.bio}
                    </ListGroupItemText>
                </ListGroupItem> */}
                <ListGroupItem>
                <ListGroupItemHeading>Population</ListGroupItemHeading>
                    <ListGroupItemText>
                    {items.population}
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                  <ListGroupItemHeading>Median Income</ListGroupItemHeading>
                  <ListGroupItemText>
                  {items.median_hh_income}
                  </ListGroupItemText>
                </ListGroupItem>
        </ListGroup>
        
        </center>
        
        </div>
      );
    }
}

export default StatesInstance;
