import React, {Component} from 'react';
import * as d3 from "d3";

class Map extends Component {
    
    constructor(props) {
        super(props);
        this.democrat = 0;
        this.republican = 0;
        this.other = 0;
        this.pages = 1;
    }


    componentDidMount() {
        var value = window.location.href.split("/")
        var path = (value[4])
        var page = 1;
        for(var i = 1; i < 173; i++){
            fetch('https://api.empoweringknowledge.me/api/congressmembers?page=' + page)
            .then(res => res.json())
            .then((data) => {
                for(var d of data.objects){
                    if(d.party == "D"){
                        
                        this.democrat += Object.keys(d.bills_sponsored).length
                       
                    }
                    else if(d.party == "R"){
                        this.republican += Object.keys(d.bills_sponsored).length
                    }
                    else {
                        this.other += Object.keys(d.bills_sponsored).length
                    }
                }
            }, this.finish())
        }
        
    }

    finish(){
        console.log("finish")
        return
    }

  

    render() {
        
        console.log(this.democrat)
        console.log(this.republican)
        console.log("done")



        // Create dummy data
        var data = {republican: this.republican, democrat: this.democrat, other: this.other}

        var width = window.screen.width*.5
    var height = 450
    var margin = 40

    // The radius of the pieplot is half the width or half the height (smallest one). I subtract a bit of margin.
    var radius = Math.min(width, height) / 2 - margin

    // append the svg object to the div called 'my_dataviz'
    var svg = d3.select("#body10")
      .append("svg")
      .attr("width", width)
      .attr("height", height)
      .append("g")
      .attr("transform", "translate(" + width / 2 + "," + height / 2 + ")");

    // set the color scale
    var color = d3.scaleOrdinal()
      .domain(data)
      .range(["#3385ff", "#8a89a6", "#FF5A5E"]);

    // Compute the position of each group on the pie:
    var pie = d3.pie()
      .value(function (d) { return d.value; })
    var data_ready = pie(d3.entries(data))
    // Now I know that group A goes from 0 degrees to x degrees and so on.

    // shape helper to build arcs:
    var arcGenerator = d3.arc()
      .innerRadius(0)
      .outerRadius(radius)

    // Build the pie chart: Basically, each part of the pie is a path that we build using the arc function.
    svg
      .selectAll('mySlices')
      .data(data_ready)
      .enter()
      .append('path')
      .attr('d', arcGenerator)
      .attr('fill', function (d) { return (color(d.data.key)) })
      .attr("stroke", "black")
      .style("stroke-width", "2px")
      .append("title")
      .text(d => `Total Donations: ${d.data.value.toLocaleString()}`);

    // Now add the annotation. Use the centroid method to get the best coordinates
    svg
      .selectAll('mySlices')
      .data(data_ready)
      .enter()
      .append('text')
      .text(function (d) { return d.data.key })
      .attr("transform", function (d) { return "translate(" + arcGenerator.centroid(d) + ")"; })
      .style("text-anchor", "middle")
      .style("font-size", 17)

        return (
            <div style={{ justifyContent: "center" }}>
                <svg id='body10' width="100%" height={650}></svg>
            </div>

        );

    }
}

export default Map;
