import React, {Component} from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Table,Col,
     } from 'reactstrap';
export class EnergyBills extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            isLoading: false,
        }
        
        
    }
    componentDidMount() {
        this.setState({isLoading: true});
        fetch('http://3.84.54.129:5000/api/bills')
        .then(res => res.json())
        .then((data) => {
            this.setState({ items: data.objects,isLoading: false});      
        })
        .catch(console.log)
    }
    
    render() {
        const {items, isLoading} = this.state;
        console.log(isLoading)
        if (isLoading){
            console.log(isLoading)
            return false;
        }
        
        console.log(items)        
        
      return (
        <div className="EnergyBills" style={{paddingTop: "2%",width:"100%", height:"60%"}}>
           <div>
            <h4>Energy Bills</h4>
            <Col sm={{ size: 'auto'}} style={{paddingTop: "2%"}}>
            <Table hover responsive>
                <thead>
                <tr>
                    <th>#</th>
                    <th>Name</th>
                    <th>Sponsor/Cosponsor</th>
                    <th>Year Introduced</th>
                    <th>Margin it was passed/defeated</th>
                    <th>Partisanship of sponsor/cosponsors</th>
                    <th>Chamber of Origin</th>
                </tr>
                </thead>
                <tbody>
                <tr>
                    <th scope="row">1</th>
                    <td> <a href= {"/bill/" + this.state.savings} class="nav-link"  to={"/politician/" + this.state.savings}>S. 1685: Launching Energy Advancement and Development through Innovations for Natural Gas Act of 2019</a></td>
                    <td> John Cornyn</td>
                    <td> 2019</td>
                    <td> N/A</td>
                    <td> 1 Republican</td>
                    <td>Senate</td>
                </tr>
                <tr>
                    <th scope="row">2</th>
                    <td> <a href= {"/bill/" + this.state.water} class="nav-link"  to={"/politician/" + this.state.water}>H.R. 2960 Energy and Water Development and Related Agencies Appropriations Act, 2020</a></td>
                    <td> Marcy Kaptur</td>
                    <td> 2019</td>
                    <td>  N/A</td>
                    <td> 1 Democrat</td>
                    <td> House</td>

                </tr>
                <tr>
                    <th scope="row">3</th>
                    <td> <a href= {"/bill/" + this.state.renewable} class="nav-link"  to={"/politician/" + this.state.renewable}>H.R.2746 - American Renewable Energy and Efficiency Act</a></td>
                    <td> Peter Welch, Ben Lujan, Barbara Lee, Yvette Clarke, Ro Khanna, Mike Quigley, Mark Pocan, Ted Lieu, Matt Cartwright, Alan Lowenthal, Joseph Crowley</td>
                    <td>2017</td>
                    <td> N/A</td>
                    <td> 11 Democrat</td>
                    <td> House</td>
                </tr>
                </tbody>
            </Table>
                </Col>
            </div>
       
        </div>
      );
    }
}

export default EnergyBills;
