import React, {Component} from "react";
import {
    Col,
    Card, CardImg, CardBody, CardTitle, CardSubtitle, CardDeck, 
    Button, 
    Pagination, PaginationItem, PaginationLink
     } from 'reactstrap';
import { BrowserRouter} from 'react-router-dom';

import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {RepresentativeInstance} from './RepresentativeInstance.js';
import person from './emptyperson.png';

export class Representatives extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            page: 1,
            pages: 0,
            nextpage: 2,
            lastpage: 1,
            sortBy: "asc",
            filter: "",
            isLoading: false,
        }
        
        
    }
    componentDidMount() {
        console.log(this.state.page)
        this.setState({isLoading: true});
        var filter = this.state.filter;
        // var population = this.state.population;
        // var median = this.state.median;

        // if(population !== ""){
        //     if(filter === ""){
        //         filter += '{"name":"population","op":"gt","val":"'+ population +'"}'
        //     }
        //     else{
        //         filter += ',{"name":"population","op":"gt","val":"'+ population +'"}'
        //     }
        // }
        // if(median !== ""){
        //     if(filter === ""){
        //         filter += '{"name":"median_hh_income","op":"gt","val":"'+ median +'"}'
        //     }
        //     else{
        //         filter += ',{"name":"median_hh_income","op":"gt","val":"'+ median +'"}'
        //     }

        //}
        if(filter === ""){
            fetch('https://api.empoweringknowledge.me/api/congressmembers?q={"order_by":[{"field":"first_name","direction":"'
                + this.state.sortBy 
                + '"}]}&page='
                + this.state.page)
            .then(res => res.json())
            .then((data) => {
                this.setState({ items: data.objects, pages:data.total_pages,isLoading: false});      
            })
            .catch(console.log)
        }
        else{
            fetch('https://api.empoweringknowledge.me/api/congressmembers?q={"filters":[' + filter + '],"order_by":[{"field":"name","direction":"'
                + this.state.sortBy 
                + '"}]}&page='
                + this.state.page)
            .then(res => res.json())
            .then((data) => {
                this.setState({ items: data.objects, pages:data.total_pages,isLoading: false});      
            })
            .catch(console.log)
        }
    }

    refetch(){
        console.log(this.state.page)
        this.setState({isLoading: true});
        fetch('https://dm5v6ty0dwipl.cloudfront.net/api/congressmembers'+ "?page=" + this.state.page)
        .then(res => res.json())
        .then((data) => {
            this.setState({ items: data.objects,isLoading: false,pages:data.total_pages});      
        })
        .catch(console.log)
    }

    handlePageClick(newPage){
        if(newPage === 1){
            this.setState({lastpage: newPage, page: newPage, nextpage: 2}, () => {
            this.componentDidMount();
            });
        }
        else if(newPage === this.state.pages){
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage}, () => {
                this.componentDidMount();
                });
        }
        else{
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage + 1}, () => {
                this.componentDidMount();
                });
        }
    }

    handleSorting(sort){
        this.setState({sortBy: sort}, () => {
        this.componentDidMount();
        });

    }

    // handleFilterPopulation(populationFilter){
    //     this.setState({population: populationFilter}, () =>{
    //     this.componentDidMount();
    //     });
    // }

    // handleFilterMedian(medianFilter){
    //     this.setState({median: medianFilter}, () =>{
    //     this.componentDidMount();
    //     });
    // }

    // handleReset(){
    //     this.setState({filter: "", population: "", median: ""}, () =>{
    //     this.componentDidMount();
    //     });
    // }

    renderSortAndFilter(){
        return(
            <div className="text-right">
                <Button color="primary" onClick={() => this.handleSorting("asc")}>Ascending</Button>{' '}
                <Button color="secondary"onClick={() => this.handleSorting("desc")}>Descending</Button>{' '}
            </div>
        );
    }
    
    render() {
        var stateMap = {
            "AL": "Alabama",
            "AK": "Alaska",
            "AZ": "Arizona",
            "AR": "Arkansas",
            "CA": "California",
            "CO": "Colorado",
            "CT": "Connecticut",
            "DE": "Delaware",
            "FL": "Florida",
            "GA": "Georgia",
            "HI": "Hawaii",
            "ID": "Idaho",
            "IL": "Illinois",
            "IN": "Indiana",
            "IA": "Iowa",
            "KS": "Kansas",
            "KY": "Kentucky",
            "LA": "Louisiana",
            "ME": "Maine",
            "MD": "Maryland",
            "MA": "Massachusetts",
            "MI": "Michigan",
            "MN": "Minnesota",
            "MS": "Mississippi",
            "MO": "Missouri",
            "MT": "Montana",
            "NE": "Nebraska",
            "NV": "Nevada",
            "NH": "New Hampshire",
            "NJ": "New Jersey",
            "NM": "New Mexico",
            "NY": "New York",
            "NC": "North Carolina",
            "ND": "North Dakota",
            "OH": "Ohio",
            "OK": "Oklahoma",
            "OR": "Oregon",
            "PA": "Pennsylvania",
            "RI": "Rhode Island",
            "SC": "South Carolina",
            "SD": "South Dakota",
            "TN": "Tennessee",
            "TX": "Texas",
            "UT": "Utah",
            "VT": "Vermont",
            "VA": "Virginia",
            "WA": "Washington",
            "WV": "West Virginia",
            "WI": "Wisconsin",
            "WY": "Wyoming",
          };
        const {items, isLoading} = this.state;
        console.log(isLoading)
        if (isLoading){
            console.log(isLoading)
            return false;
        }
        
        console.log(items)        
        
      return (
        <div>
            <div>
                {this.renderSortAndFilter()}
            </div>
            <BrowserRouter>
            <div className="Representatives" style={{width:"100%", height:"60%"}}>
            
               <div>

                <Col sm={{ size: 'auto'}}>
                    <CardDeck>
                        {items.slice(0, 3).map(item =>
                            <Card>
                                <CardImg top width="80%" src={item.image_url}  alt={"No image"} />
                                <CardBody>
                                <CardTitle>{item.first_name + " " + item.last_name}</CardTitle>
                                <CardSubtitle>State: {stateMap[item.state]}</CardSubtitle>
                                <CardSubtitle>Party: {item.party}</CardSubtitle>
                                <CardSubtitle>Title:  {item.title}</CardSubtitle>
                                <CardSubtitle>District:  {item.district}</CardSubtitle>
                                <Button href= {"/politician/" + item.member_id} class="nav-link"  to={"/politician/" + item.member_id} render={(props) => <RepresentativeInstance {...props} name={item.member_id} />}> See More</Button>
                                </CardBody>
                                
                            </Card>
                        )}
                    </CardDeck>
                    <CardDeck>
                        {items.slice(3, 6).map(item =>
                            <Card>
                                <CardImg top width="80%" src={item.image_url} alt={"No image"} />
                                <CardBody>
                                <CardTitle>{item.first_name + " " + item.last_name}</CardTitle>
                                <CardSubtitle>State: {item.state}</CardSubtitle>
                                <CardSubtitle>Party: {item.party}</CardSubtitle>
                                <CardSubtitle>Title:  {item.title}</CardSubtitle>
                                <CardSubtitle>District:  {item.district}</CardSubtitle>
                                    <Button href= {"/politician/" + item.member_id} class="nav-link"  to={"/politician/" + item.member_id} render={(props) => <RepresentativeInstance {...props} name={item.member_id} />}> See More</Button>
                                </CardBody>
                                
                            </Card>
                        )}
                    </CardDeck>
                    <CardDeck>
                        {items.slice(6, 10).map(item =>
                            <Card>
                                <CardImg top width="80%" src={item.image_url} /*onError={(e)=>{e.target.onerror = null; e.target.src={person}}}*/ alt={"No image"} />
                                <CardBody>
                                <CardTitle>{item.first_name + " " + item.last_name}</CardTitle>
                                <CardSubtitle>State: {item.state}</CardSubtitle>
                                <CardSubtitle>Party: {item.party}</CardSubtitle>
                                <CardSubtitle>Title:  {item.title}</CardSubtitle>
                                <CardSubtitle>District:  {item.district}</CardSubtitle>
                                <Button href= {"/politician/" + item.member_id} class="nav-link"  to={"/politician/" + item.member_id} render={(props) => <RepresentativeInstance {...props} name={item.member_id} />}> See More</Button>
                                </CardBody>
                                
                            </Card>
                        )}
                    </CardDeck>

                </Col>

                </div>
                <div>
            <Pagination size="lg" style={{justifyContent: 'center', paddingTop:'3%'}} aria-label="Page navigation example">
            <PaginationItem>
              <PaginationLink first onClick={() => this.handlePageClick(1)} href={"#"} />
            </PaginationItem>
            <PaginationItem>
              <PaginationLink previous onClick={() => this.handlePageClick(this.state.lastpage)} href={"#"} />
            </PaginationItem>
            {/* <PaginationItem>
              <PaginationLink onClick={() => this.handlePageClick(this.state.lastpage)} href={"#"}>
                {this.state.lastpage}
              </PaginationLink>
            </PaginationItem> */}
            <PaginationItem>
              <PaginationLink onClick={() => this.handlePageClick(this.state.page)} href={"#"}>
                {this.state.page}
              </PaginationLink>
            </PaginationItem>
            <PaginationItem>
              <PaginationLink onClick={() => this.handlePageClick(this.state.nextpage)} href={"#"}>
                {this.state.nextpage}
              </PaginationLink>
            </PaginationItem>
            <PaginationItem>
              <PaginationLink next onClick={() => this.handlePageClick(this.state.nextpage)} href={"#"} />
            </PaginationItem>
            <PaginationItem>
              <PaginationLink last onClick={() => this.handlePageClick(this.state.pages)} href="#" />
            </PaginationItem>
          </Pagination>
          </div>
            </div>
            </BrowserRouter>
        </div>

      );
    }
}

export default Representatives;
