import React, {Component} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Timeline } from 'react-twitter-widgets'

import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    UncontrolledDropdown, ListGroup, ListGroupItem, ListGroupItemHeading, ListGroupItemText,
    DropdownToggle,
    DropdownMenu,
    DropdownItem } from 'reactstrap';

export class RepresentativeInstance extends Component {
  constructor(props) {
    super(props);
    this.state = {
        items: [],
        isLoading: false,
        twitter: "",
    }
    
    
}
componentDidMount() {
    var value = window.location.href.split("/")
    var path = (value[4])
    console.log('http://3.84.54.129:5000/api/congressmembers/' + path)
    this.setState({isLoading: true});
    fetch('http://3.84.54.129:5000/api/congressmembers/' + path)
    .then(res => res.json())
    .then((data) => {
        this.setState({ items: data,isLoading: false});      
    })
    .catch(console.log)
}

render() {

    const {items, isLoading} = this.state;
    
    console.log(isLoading)
    if (isLoading){
        console.log(isLoading)
        return false;
    }
    this.state.twitter = items.twitter
    if(items.twitter == null){
        this.state.twitter = "twitter" 
    }
    console.log(items)        
    
      return (
        
        <div center className="RepresentativeInstance"style = {{paddingLeft:'3%',textAlign: 'center',color: '#000000'}}>
            <img style={{paddingTop:'4%',width: '260px', height: '375px'}} src={"https://theunitedstates.io/images/congress/original/" + items.member_id + ".jpg"} alt={"hi"}/>
            <h2 style = {{paddingTop: "3%",color: '#000000'}}> {items.first_name} {items.last_name} </h2>
            <center>
            <ListGroup style = {{paddingTop:'1%', paddingLeft:'3%',textAlign: 'center',color: '#000000', width: '50%'}}>
            {/* <ListGroupItem>
                <ListGroupItemHeading>Summary</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.state.bio}
                    </ListGroupItemText>
                </ListGroupItem> */}
                <ListGroupItem>
                <ListGroupItemHeading>State</ListGroupItemHeading>
                    
                    <ListGroupItemText>
                    <a href = {'/state/'+items.state}>{items.state}</a>
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                  <ListGroupItemHeading>District</ListGroupItemHeading>
                  <ListGroupItemText>
                  {items.district}
                  </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Party</ListGroupItemHeading>
                <ListGroupItemText>
                {items.party}
                </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Date Of Birth</ListGroupItemHeading>
                    <ListGroupItemText>
                    {items.date_of_birth}
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Title</ListGroupItemHeading>
                    <ListGroupItemText>
                    {items.title}
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Bills</ListGroupItemHeading>
                    <ListGroupItemText>
                        {/* <a href={bill}>{billname}</a> */}
                    </ListGroupItemText>
                </ListGroupItem>
        </ListGroup>
        </center>
            <div>
                <Timeline
                dataSource={{
                sourceType: 'profile',
                screenName: items.twitter
                }}
                options={{
                height: '400'
            }}/>
        </div>
        </div>
        

      );
    }
}

export default RepresentativeInstance;
