import React, {Component} from 'react';
import * as d3 from "d3";

class Map extends Component {
    
    constructor(props) {
        super(props);
        this.democrat = 0;
        this.republican = 0;
        this.other = 0;
        this.pages = 1;
        this.getData = this.getData.bind(this);
        this.getPopulationPerState = this.getPopulationPerState.bind(this);
    }

    componentDidMount() {
        var value = window.location.href.split("/")
        var path = (value[4])
        var page = 1;
        for(var i = 1; i < 173; i++){
            fetch('https://api.empoweringknowledge.me/api/representatives/?page=' + page)
            .then(res => res.json())
            .then((data) => {
                // return this.getMembers(data)
                if(data.party == "democrat"){
                    this.democrat += Object.keys(data.bills_sponsored).length
                }
                if(data.party == "republican"){
                    this.republican += Object.keys(data.bills_sponsored).length
                }
                else{
                    this.other += Object.keys(data.bills_sponsored).length
                }
            })
        }
        
    }


  

    render() {
        var width = 450
        var height = 450
        var margin = 40

        // The radius of the pieplot is half the width or half the height (smallest one). I subtract a bit of margin.
        var radius = Math.min(width, height) / 2 - margin

        // append the svg object to the div called 'my_dataviz'
        var svg = d3.select("#my_dataviz")
        .append("svg")
            .attr("width", width)
            .attr("height", height)
        .append("g")
            .attr("transform", "translate(" + width / 2 + "," + height / 2 + ")");

        // Create dummy data
        var data = {republican: this.republican, democrat: this.democrat, other: this.other}

        // set the color scale
        var color = d3.scaleOrdinal()
        .domain(data)
        .range(["#98abc5", "#8a89a6", "#7b6888", "#6b486b", "#a05d56"])

        // Compute the position of each group on the pie:
        var pie = d3.pie()
        .value(function(d) {return d.value; })
        var data_ready = pie(d3.entries(data))

        // Build the pie chart: Basically, each part of the pie is a path that we build using the arc function.
        svg
        .selectAll('whatever')
        .data(data_ready)
        .enter()
        .append('path')
        .attr('d', d3.arc()
            .innerRadius(0)
            .outerRadius(radius)
        )
        .attr('fill', function(d){ return(color(d.data.key)) })
        .attr("stroke", "black")
        .style("stroke-width", "2px")
        .style("opacity", 0.7)

        var color = 0;
        return (
            <HeatMapDate
                data={this.getData}
                // colors={'#232323'}
                defaultColor
            />
        );

    }
}

export default Map;
