import React, {Component} from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Table,Col,
     } from 'reactstrap';
export class EnergyBills extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            isLoading: false,
        }
        
        
    }
    componentDidMount() {
        this.setState({isLoading: true});
        fetch('http://3.84.54.129:5000/api/bills')
        .then(res => res.json())
        .then((data) => {
            this.setState({ items: data.objects,isLoading: false});      
        })
        .catch(console.log)
    }
    
    render() {
        const {items, isLoading} = this.state;
        console.log(isLoading)
        if (isLoading){
            console.log(isLoading)
            return false;
        }
        
        console.log(items)        
        
      return (
        <div className="EnergyBills" style={{paddingTop: "2%",width:"100%", height:"60%"}}>
           <div>
            <h4>Energy Bills</h4>
            <Col sm={{ size: 'auto'}} style={{paddingTop: "2%"}}>
            <Table hover responsive>
                <thead>
                <tr>
                    <th>#</th>
                    <th>Title</th>
                    <th>Sponsor</th>
                    <th>Introduced Date</th>
                    <th>House Passage</th>
                    <th>Senate Passage</th>
                    <th>Democrat Cosponsors</th>
                    <th>Republican Cosponsors</th>
                </tr>
                </thead>
                <tbody>
                {items.map(item =>
                
                <tr>
                    console.log(item)
                    <th scope="row"></th>
                    <td> <a href= {"/bill/" + item.bill_id}   to={"/bill/" + item.bill_id}>{item.bill_title}</a></td>
                    <td> <a href= {"/politician/" + item.sponsor_id}   to={"/politician/" + item.sponsor_id}>{item.sponsor_id}</a></td>
                    <td> {item.itroduced_date}</td>
                    <td> {item.house_passage}</td>
                    <td> {item.senate_passage}</td>
                    <td> {item.cosponsors_by_party_D}</td>
                    <td> {item.cosponsors_by_party_R}</td>
                </tr>
                )}
                </tbody>
            </Table>
                </Col>
            </div>
       
        </div>
      );
    }
}

export default EnergyBills;
