import React, {Component} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Timeline } from 'react-twitter-widgets'
import ted from './ted.jpg';
import jeff from './small_merkley_photo.jpg';
import aoc from './aoc.jpg';
import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    UncontrolledDropdown, ListGroup, ListGroupItem, ListGroupItemHeading, ListGroupItemText,
    DropdownToggle,
    DropdownMenu,
    DropdownItem } from 'reactstrap';

export class RepresentativeInstance extends Component {
    constructor(props) {
      super(props);
      this.state = {
        bio:"",
        twitter:"",
      };
    }

    render() {
        var image;
        var twitter;
        var state;
     console.log(this.props.name)
      if(this.props.name == "Marcy Kaptur"){
        image = "marcy";
        twitter = "RepMarcyKaptur";
        state = "/state/ohio";
        this.state.bio = "Marcia Carolyn Kaptur is the U.S. Representative for Ohio's 9th congressional district and a Democrat. The district stretches from Kaptur's hometown of Toledo to Cleveland. It includes all of Ottawa and Erie counties, and parts of Lucas, Lorain, and Cuyahoga counties."
      }
      else if(this.props.name == "Peter Welch"){
        image = "peterWelch";
        twitter = "PeterWelch";
        state = "/states";
        this.state.bio = "Peter Francis Welch is an American politician serving as the U.S. Representative for Vermont's at-large congressional district since 2007. He is a member of the Democratic Party and has been a major figure in Vermont politics for over three decades"
      }
      else{
        image = "aoc";
        twitter = "RepAOC";
        state = "/state/newyork";
        this.state.bio = "Alexandria Ocasio-Cortez (born October 13, 1989), often referred to by her initials, AOC, is an American politician and activist who serves as the U.S. Representative for New York's 14th congressional district. The district includes the eastern part of the Bronx and portions of north-central Queens in New York City. She is a member of the Democratic Party."
      }
      return (
        <div center className="RepresentativeInstance"style = {{paddingLeft:'3%',textAlign: 'center',color: '#000000'}}>
            <img style={{paddingTop:'4%',width: '260px', height: '375px'}} src={require('./'+image+'.jpg')}/>
            <h2 style = {{paddingTop: "3%",color: '#000000'}}> {this.props.name} </h2>
            <center>
            <ListGroup style = {{paddingTop:'1%', paddingLeft:'3%',textAlign: 'center',color: '#000000', width: '50%'}}>
            <ListGroupItem>
                <ListGroupItemHeading>Summary</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.state.bio}
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>State</ListGroupItemHeading>
                    
                    <ListGroupItemText>
                    <a href = {state}>{this.props.state}</a>
                    </ListGroupItemText>
                    </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Party</ListGroupItemHeading>
                <ListGroupItemText>
                {this.props.party}
                </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Years Active</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.props.date}
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Chamber</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.props.chamber}
                    </ListGroupItemText>
                </ListGroupItem>
        </ListGroup>
        </center>
            <div>
                <Timeline
                dataSource={{
                sourceType: 'profile',
                screenName: twitter
                }}
                options={{
                height: '400'
            }}/>
        </div>
        </div>
        

      );
    }
}

export default RepresentativeInstance;
