import React, {Component, useState} from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter} from 'react-router-dom';
import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    Col, Button, Form, FormGroup, Label, Input
   } from 'reactstrap';

  
const Navigation = (props) => {
  
  const [isOpen, setIsOpen] = useState(false);
  const toggle = () => setIsOpen(!isOpen);

  this.handleTermChange = this.handleTermChange.bind(this);
  this.handleSearch = this.handleSearch.bind(this);
  this.handleEnter = this.handleEnter.bind(this);
  this.handleSubmit = this.handleSubmit.bind(this);

  this.handleTermChange=(e) => {
    this.setState({ term: e.target.value });
  }

  this.handleSearch=() => {
      this.props.searchEngine(this.state.term);
  }

  this.handleEnter=(e) => {
      if (e.key === 13) {
      this.handleSearch();
      }
  }

  this.handleSubmit=(e) => {
      e.preventDefault();
      this.handleSearch();
  }
   
    return (
      <div className = "Navbar">
      <Navbar color = "dark" dark expand = "md">
        <NavbarBrand style = {{color: '#c7cfd9', textDecoration: 'none'}} href = "/">Empowering Knowledge</NavbarBrand>
        <NavbarToggler onClick={toggle} className="mr-2" />
        <Collapse isOpen={isOpen} navbar>
          <Nav className ="ml-auto" navbar>
            <NavItem>
              <BrowserRouter>
                  <NavLink  href="/about" className="nav-link">About</NavLink>
              </BrowserRouter>
            </NavItem>
            <NavItem>
              <BrowserRouter>
                  <NavLink href="/politicians" className="nav-link">Politicians</NavLink>
              </BrowserRouter>
            </NavItem>
            <NavItem>
              <BrowserRouter>
                  <NavLink href="/states" className="nav-link">States</NavLink>
              </BrowserRouter>
            </NavItem>
            <NavItem>
              <NavLink href="/bills" className="nav-link">Energy Bills</NavLink>
            </NavItem> 
            <NavItem>
              <Form className="searchbox" onSubmit={this.handleSubmit}>
                <FormGroup row>
                <Label for="searchId" sm={2}>
                    Search
                </Label>
                <Col sm={10}>
                    <Input
                    type="text"
                    placeholder="Search this site"
                    onChange={this.handleTermChange}
                    onKeyDown={this.handleEnter}
                    />
                </Col>
                </FormGroup>

                <FormGroup>
                <Col sm="2">
                    <div className="">
                    <Button
                        onClick={this.handleSearch}
                        className="btn"
                    >
                        Submit
                    </Button>
                    </div>
                </Col>
                </FormGroup>
                </Form>
            </NavItem>
          </Nav>
          </Collapse>
      </Navbar>
    
      
    </div>
    );
}

export default Navigation;
