import React, {Component} from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {Route, Switch} from 'react-router-dom';
import {RepresentativeInstance} from './RepresentativeInstance.js';
import {
    Col,
    Card, CardImg, CardBody,
    CardTitle, CardSubtitle, Button, CardDeck,
     } from 'reactstrap';
import { BrowserRouter} from 'react-router-dom';
export class Representatives extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            isLoading: false,
        }
        
        
    }
    componentDidMount() {
        this.setState({isLoading: true});
        // fetch('http://3.84.54.129/api/congressmembers')
        fetch('http://127.0.0.1/api/congressmembers')
        .then(res => res.json())
        .then((data) => {
            this.setState({ items: data.objects,isLoading: false});      
        })
        .catch(console.log)
    }
    
    render() {

        const {items, isLoading} = this.state;
        console.log(isLoading)
        if (isLoading){
            console.log(isLoading)
            return false;
        }
        
        console.log(items)        
        
      return (
        <BrowserRouter>

        <div className="Representatives" style={{width:"100%", height:"60%"}}>
        
           <div>

            <Col sm={{ size: 'auto'}}>
                

                <CardDeck>
                    {items.map(item =>
                        
                        <Card>
                            <CardImg top width="80%" src={'https://theunitedstates.io/images/congress/A000014.jpg'} alt="Card image cap" />
                            <CardBody>
                            <CardTitle>{item.first_name}{item.last_name}</CardTitle>
                            <CardSubtitle>State: {item.state}</CardSubtitle>
                            <CardSubtitle>Party: {item.party}</CardSubtitle>
                            <CardSubtitle>Title:  {item.title}</CardSubtitle>
                                <Button href= {"/politician/" + item.member_id} class="nav-link"  to={"/politician/" + item.member_id}> See More</Button>
                            </CardBody>
                            
                        </Card>
                    )}
                </CardDeck>

            </Col>

            </div>
           
        </div>
        </BrowserRouter>

      );
    }
}

export default Representatives;
