import React, {Component} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import texas from './texas.png';
import california from './california.png';
import newYork from './new_York.png';
import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand, ListGroup, ListGroupItem, ListGroupItemHeading, ListGroupItemText,
    Nav,
    NavItem,
    NavLink,
    UncontrolledDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem } from 'reactstrap';
import {Link} from 'react-router-dom';

export class StatesInstance extends Component {
    constructor(props) {
      super(props);
      this.state = {
        bio:"",
    
      };
    }

    render() {
        var image;
        var image1;
        var rep;
     console.log(this.props.name)
      if(this.props.name == "Vermont"){
        image = "vaflag";
        image1 = "vermont";
        this.state.bio = "Fuel Oil, Ethanol, Biomass";
        rep = "peterwelch"
      }
      else if(this.props.name == "Ohio"){
        image = "oiflag";
        image1 = "ohio"
        this.state.bio = "Natural Gas, Coal, Ethanol"
      }
      else{
        image = "txflag";
        image1 = "texas"
        this.state.bio = "Natural Gas, Ethanol, Jet Fuel"
      }
      return (
        <div center className="StatesInstance"style = {{paddingLeft:'3%',textAlign: 'center',color: '#000000'}}>
            <h2 style = {{fontSize: '40px',paddingTop: "1%",color: '#000000'}}> {this.props.name} </h2>
            <img style={{paddingTop:'2%', width: '300px', height: '375px'}} src={require('./'+image1+'.png')}/>
            <img style={{paddingTop:'2%', paddingLeft:'4%'}} src={require('./'+image+'.svg.png')}/>

            <center>
            <ListGroup style = {{paddingTop:'3%', paddingLeft:'3%',textAlign: 'center',color: '#000000', width: '50%'}}>
            <ListGroupItem>
                <ListGroupItemHeading>Major Energy Source</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.state.bio}
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Population</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.props.population}
                    </ListGroupItemText>
                    </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>GDP</ListGroupItemHeading>
                <ListGroupItemText>
                {this.props.gdp}
                </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Region</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.props.region}
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Dominant Political Party</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.props.party}
                    </ListGroupItemText>
                </ListGroupItem>
        </ListGroup>
        </center>
        </div>
      );
    }
}

export default StatesInstance;
