import React, {Component} from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Col,
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button, CardDeck,
    DropdownMenu,
    DropdownItem } from 'reactstrap';
import {BrowserRouter} from 'react-router-dom';
export class States extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            isLoading: false,
        }
        
        
    }
    componentDidMount() {
        this.setState({isLoading: true});
        fetch('http://3.84.54.129:5000/api/states')
        .then(res => res.json())
        .then((data) => {
            this.setState({ items: data.objects,isLoading: false});      
        })
        .catch(console.log)
    }
    
    render() {

        const {items, isLoading} = this.state;
        console.log(isLoading)
        if (isLoading){
            console.log(isLoading)
            return false;
        }
        
        console.log(items)        
        
      return (
        <div className="States" style={{paddingTop: "100px",width:"100%", height:"60%"}}>
           <div>
           <Col sm={{ size: 'auto'}}>
                <CardDeck>
                    {items.slice(0, 3).map(item =>
                        <Card>
                            <CardImg top width="80%" src={"https://geology.com/county-map/"+item.name+"-county-map.gif"} alt={"No image"} />
                            <CardBody>
                            <CardTitle>{item.name}</CardTitle>
                            <CardSubtitle>State: {item.state}</CardSubtitle>
                            <CardSubtitle>Party: {item.party}</CardSubtitle>
                            <CardSubtitle>Title:  {item.title}</CardSubtitle>
                            <Button href= {"/politician/" + item.member_id} class="nav-link"  to={"/politician/" + item.member_id}> See More</Button>
                            </CardBody>
                            
                        </Card>
                    )}
                </CardDeck>
                <CardDeck>
                    {items.slice(3, 6).map(item =>
                        <Card>
                            <CardImg top width="80%" src={"https://geology.com/county-map/"+item.name+"-county-map.gif"} alt={"No image"} />
                            <CardBody>
                            <CardTitle>{item.first_name + " " + item.last_name}</CardTitle>
                            <CardSubtitle>State: {item.state}</CardSubtitle>
                            <CardSubtitle>Party: {item.party}</CardSubtitle>
                            <CardSubtitle>Title:  {item.title}</CardSubtitle>
                                <Button href= {"/politician/" + item.member_id} class="nav-link"  to={"/politician/" + item.member_id} > See More</Button>
                            </CardBody>
                            
                        </Card>
                    )}
                </CardDeck>
                <CardDeck>
                    {items.slice(6, 10).map(item =>
                        <Card>
                            <CardImg top width="80%" src={"https://geology.com/county-map/"+item.name+"-county-map.gif"} alt={"No image"} />
                            <CardBody>
                            <CardTitle>{item.first_name + " " + item.last_name}</CardTitle>
                            <CardSubtitle>State: {item.state}</CardSubtitle>
                            <CardSubtitle>Party: {item.party}</CardSubtitle>
                            <CardSubtitle>Title:  {item.title}</CardSubtitle>
                            <Button href= {"/state/" + item.member_id} class="nav-link"  to={"/politician/" + item.member_id}> See More</Button>
                            </CardBody>
                            
                        </Card>
                    )}
                </CardDeck>

            </Col>
            </div>
       
        </div>
      );
    }
}

export default States;
