import React, {Component} from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Table,Col, Pagination, PaginationItem, PaginationLink, Form, FormGroup, Label, Input, Button
     } from 'reactstrap';
export class EnergyBills extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            page: 1,
            pages: 0,
            nextpage: 2,
            lastpage: 1,
            isLoading: false,
            input: "",
        }
        this.handleTermChange = this.handleTermChange.bind(this);
        this.handleEnter = this.handleEnter.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleSearch = this.handleSearch.bind(this);
    }
    componentDidMount() {
        this.setState({isLoading: true});
        var val;
        fetch('https://api.empoweringknowledge.me/api/bills'+ "?page=" + this.state.page)
        .then(res => res.json())
        .then((data) => {
            val = data.objects
            this.setState({ items: data.objects,isLoading: false,pages:data.total_pages});      
        })
        .catch(console.log)
      
    }

    loadData(){
      var total = 300;
      for(var i = 0; i < total; i++){
          fetch('https://api.empoweringknowledge.me/api/bills'+ "?page=" + i)
          .then(res => res.json())
          .then((data) => {
              total = data.total_pages
              this.setState({ bills: data.objects});      
          })
          .catch(console.log)
      }
  }

  getBillResults=  () => {
      let searchTerms = [...new Set(this.props.name.toLowerCase().trim().split(" "))];
      var billData;
      searchTerms.map(query => 
          billData.add(this.state.bills.filter((bill) => {
              return bill.committees.toLowerCase().includes(query)
              || bill.introduced_date.toLowerCase().includes(query) || bill.senate_passage.toLowerCase().includes(query) ||
              bill.title.toLowerCase().includes(query) || bill.sponsor_id.toLowerCase().includes(query)
          }))
      )
      var array = [];
      for (var x = 0; x < billData.length; x++) {
          array.push(
              <tr>
                  <th scope="row"></th>
                  <td> {billData[x].title} <Highlighter
                  highlightClassName="HighlightClass"
                  searchWords={searchTerms}
                  autoEscape={true}
                  textToHighlight={billData[x].title}
                  />
                  </td>
                  <td> {billData[x].sponsor_id}</td>
                  <td> {billData[x].committees}</td>
                  <td> {billData[x].introduced_date}</td>
                  <td> {billData[x].house_passage}</td>
                  <td> {billData[x].senate_passage}</td>
                  <td> {billData[x].cosponsors_by_party_D}</td>
                  <td> {billData[x].cosponsors_by_party_R}</td>
              </tr>
          
          );
      }
      return array;

  };
 
    handlePageClick(newPage){
        if(newPage === 1){
            this.setState({lastpage: newPage, page: newPage, nextpage: 2}, () => {
            this.componentDidMount();
            });
        }
        else if(newPage === this.state.pages){
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage}, () => {
                this.componentDidMount();
                });
        }
        else{
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage + 1}, () => {
                this.componentDidMount();
                });
        }
        
    } 

    handleTermChange(e) {
        this.setState({ input: e.target.value });
    }

    handleSearch() {
        let searchTerms = [...new Set(this.state.input.toLowerCase().trim().split(" "))];
        this.state.items.map(item =>
          searchTerms.map(terms =>
            console.log(terms)
          
          )      
          
        )
        console.log(this.state.input)

    }

    handleEnter(e) {
        if (e.key === 13) {
        this.handleSearch();
        }
    }

    handleSubmit(e) {
        e.preventDefault();
        this.handleSearch();
   }

    render() {

        const {items, isLoading} = this.state;
        //console.log(isLoading)
        if (isLoading){
            //console.log(isLoading)
            return false;
        }
        
       // console.log(items)     
        const { input } = this.state.input;   
        
      return (
        <div className="EnergyBills" style={{paddingTop: "2%",width:"100%", height:"60%"}}>
           <div>
           <Form className="searchbox" onSubmit={this.handleSubmit}>
            <FormGroup style={{width: '50%'}}>
                <Label for="exampleEmail">Search</Label>
                <Input  /*innerRef={(node) => this.state.input = node}*/
                        value= {input}
                        onChange={this.handleTermChange}
                        onKeyDown={this.handleEnter}
                />
                <Button onClick={ (e) => {
                          this.handleSearch(e)
                        } }>Search</Button>
              </FormGroup>
            </Form>
            <h4>Energy Bills</h4>
            <Col sm={{ size: 'auto'}} style={{paddingTop: "2%"}}>
            <Table hover responsive>
                <thead>
                <tr>
                    <th>#</th>
                    <th>Title</th>
                    <th>Sponsor</th>
                    <th>Committees</th>
                    <th>Introduced Date</th>
                    <th>House Passage</th>
                    <th>Senate Passage</th>
                    <th>Democrat Cosponsors</th>
                    <th>Republican Cosponsors</th>
                </tr>
                </thead>
                <tbody>
                    
                {items.map(item =>
                  
                    <tr>
                        <th scope="row"></th>
                        <td> <a href= {"/bill/" + item.bill_id}   to={"/bill/" + item.bill_id}>{item.title}</a></td>
                        <td> <a href= {"/politician/" + item.sponsor_id}   to={"/politician/" + item.sponsor_id}>{item.sponsor_id}</a></td>
                        <td> {item.committees}</td>
                        <td> {item.introduced_date}</td>
                        <td> {item.house_passage}</td>
                        <td> {item.senate_passage}</td>
                        <td> {item.cosponsors_by_party_D}</td>
                        <td> {item.cosponsors_by_party_R}</td>
                    </tr>
                )}
                </tbody>
            </Table>
                </Col>
            </div>
        <div>
        <Pagination size="lg" style={{justifyContent: 'center', paddingTop:'3%'}} aria-label="Page navigation example">
        <PaginationItem>
          <PaginationLink first onClick={() => this.handlePageClick(1)} href={"#"} />
        </PaginationItem>
        <PaginationItem>
          <PaginationLink previous onClick={() => this.handlePageClick(this.state.lastpage)} href={"#"} />
        </PaginationItem>
        {/* <PaginationItem>
          <PaginationLink onClick={() => this.handlePageClick(this.state.lastpage)} href={"#"}>
            {this.state.lastpage}
          </PaginationLink>
        </PaginationItem> */}
        <PaginationItem>
          <PaginationLink onClick={() => this.handlePageClick(this.state.page)} href={"#"}>
            {this.state.page}
          </PaginationLink>
        </PaginationItem>
        <PaginationItem>
          <PaginationLink onClick={() => this.handlePageClick(this.state.nextpage)} href={"#"}>
            {this.state.nextpage}
          </PaginationLink>
        </PaginationItem>
        <PaginationItem>
          <PaginationLink next onClick={() => this.handlePageClick(this.state.nextpage)} href={"#"} />
        </PaginationItem>
        <PaginationItem>
          <PaginationLink last onClick={() => this.handlePageClick(this.state.pages)} href="#" />
        </PaginationItem>
      </Pagination>
      </div>
        </div>
      );
    }
}

export default EnergyBills;
