import React, {Component} from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import home from './homepage.jpg';
import './Home.css';
import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    UncontrolledDropdown,
    DropdownToggle,
    DropdownMenu, Media, Form, FormGroup, Label, Input, Button,
    DropdownItem } from 'reactstrap';
export class Home extends Component {
    constructor(props) {
      super(props);
      this.handleTermChange = this.handleTermChange.bind(this);
        this.handleEnter = this.handleEnter.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleSearch = this.handleSearch.bind(this);
        this.state.input= "";
    }

 
    

    handleTermChange(e) {
        this.setState({ input: e.target.value });
    }

    handleSearch() {
        let searchTerms = [...new Set(this.state.input.toLowerCase().trim().split(" "))];
        this.state.items.map(item =>
          searchTerms.map(terms =>
            console.log(terms)
          
          )      
          
        )
        console.log(this.state.input)

    }

    handleEnter(e) {
        if (e.key === 13) {
        this.handleSearch();
        }
    }

    handleSubmit(e) {
        e.preventDefault();
        this.handleSearch();
   }
    render() {
      const { input } = this.state.input;   
    //Photo by American Public Power Association on Unsplash
      return (
        <div className="Home" >
            <center><h3 style={{fontSize: '50px'}}>Empowering Knowledge</h3></center>
            <div className="text" >
                <p style={{textAlign: 'center', fontSize: '20px', paddingTop: '2%'}}>Do you know where your energy is coming from? Do you know who is making those decisions?</p>
            </div>
            <img style={{
                flex: 1,
                alignSelf: 'stretch',
                width: "100%",
                height: "100%"
                }}
            src={home}     
            />
            <Form className="searchbox" onSubmit={this.handleSubmit}>
            <FormGroup style={{width: '50%'}}>
                <Label for="exampleEmail">Search</Label>
                <Input  /*innerRef={(node) => this.state.input = node}*/
                        value= {input}
                        onChange={this.handleTermChange}
                        onKeyDown={this.handleEnter}
                />
                <Button onClick={ (e) => {
                          this.handleSearch(e)
                        } }>Search</Button>
              </FormGroup>
            </Form>
        </div>
        
      );
    }
}

export default Home;
