import React, {Component} from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    UncontrolledDropdown,
    DropdownToggle,  Row, Col,
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button, CardDeck,
    DropdownMenu,
    DropdownItem } from 'reactstrap';
import briana from './briana.jpg';
import edwin from './edwin.jpeg';
import ryan from './ryan.jpg';
import ojas from './ojas.jpg';
import travis from './travis.jpeg';
export class About extends Component {
    constructor(props) {
      super(props);
      this.state = {
        people: [],
      };
    }
    componentDidMount() {
        fetch('https://gitlab.com/api/v4/projects/14474532/repository/contributors')
        .then(res => res.json())
        .then((data) => {
            console.log(data);
          this.setState({ people: data });
        })
        .catch(console.log)

        fetch('https://gitlab.com/api/v4/projects/14474532/issues')
        .then(res => res.json())
        .then((info) => {
            console.log(info);
          this.setState({ issues: info });
        })
    }
    render() {
        var briana = {'commits':0, 'issues':0}
        var ojas = {'commits':0, 'issues':0}
        var travis = {'commits':0, 'issues':0}
        var edwin = {'commits':0, 'issues':0}
        var ryan = {'commits':0, 'issues':0}
        var person;
        for(person of this.state.people){
            if(person.name == "Ojas Patel"){
                ojas['commits'] += person.commits;
            } 
            else if(person.name == "Travarin"){
                travis['commits'] += person.commits;
            }
            else if(person.name == "Edwin_Silerio"){
                edwin['commits'] += person.commits;
            }
            else if(person.name == "Ryan Morris"){
                ryan['commits'] += person.commits;
            }
            else if(person.name == "brianawhite"){
                briana['commits'] += person.commits;
            }
            
        }
        var issue;
        for(issue of this.state.issues){
            if(issue.closed_by != null){
                // if(issue.closed_by.)
                ojas['issues'] += 1;
            }
        }

      return (
        <div className="About" style={{paddingTop: "100px", paddingLeft: "50px",width:"100%", height:"60%"}}>
        <div>
         <Col sm={{ size: '10'}}>
         {/* const { people } = this.state; */}
         {/* {this.state.people.map((people) => (
            <Card>
            {/* <CardImg top width="100%" src={briana} /> */}
            {/* <CardBody>
            <CardTitle>{people.name}</CardTitle>
            <CardText>Bio</CardText>
            <CardText>Major responsibilities</CardText>
            <CardText>Commits:{people.commits}</CardText>
            <CardText>Issues:</CardText>
            <CardText>Unit tests:</CardText>
            </CardBody>
        </Card>
         ))} */}
        
        </Col>
         </div>

         <CardDeck>
                 <Card>
                     <CardImg top width="100%" src={briana} />
                     <CardBody>
                     <CardTitle>{this.name}</CardTitle>
                     <CardText>Bio</CardText>
                     <CardText>Major responsibilities</CardText>
                     <CardText>Commits:</CardText>
                     <CardText>Issues:</CardText>
                     <CardText>Unit tests:</CardText>
                     </CardBody>
                 </Card>
                 <Card>
                     <CardImg top width="100%" src={edwin} alt="Card image cap" />
                     <CardBody>
                     <CardTitle>Edwin Silerio</CardTitle>
                     <CardText>Bio</CardText>
                     <CardText>Major responsibilities</CardText>
                     <CardText>Commits:</CardText>
                     <CardText>Issues:</CardText>
                     <CardText>Unit tests:</CardText>
                     </CardBody>
                 </Card>
                 <Card>
                     <CardImg top width="100%" src={ojas} alt="Card image cap" />
                     <CardBody>
                     <CardTitle>Ojas Patel</CardTitle>
                     <CardText>Bio</CardText>
                     <CardText>Major responsibilities</CardText>
                     <CardText>Commits:</CardText>
                     <CardText>Issues:</CardText>
                     <CardText>Unit tests:</CardText>
                     </CardBody>
                 </Card>
                 </CardDeck>
             <Row style={{paddingTop: "100px", paddingLeft: "75px"}} sm={{ size: '5'}}>
                 <Col sm={{ size: '10'}}>
                    <CardDeck>
                        <Card>
                            <CardImg top width="100%" src={ryan} alt="Card image cap" />
                            <CardBody>
                            <CardTitle>Ryan Morris</CardTitle>
                            <CardText>Bio</CardText>
                            <CardText>Major responsibilities</CardText>
                            <CardText>Commits:</CardText>
                            <CardText>Issues:</CardText>
                            <CardText>Unit tests:</CardText>
                            </CardBody>
                        </Card>
                        <Card>
                            <CardImg top width="100%" src={travis} alt="Card image cap" />
                            <CardBody>
                            <CardTitle>Travis Langston</CardTitle>
                            <CardText>Bio</CardText>
                            <CardText>Major responsibilities</CardText>
                            <CardText>Commits:</CardText>
                            <CardText>Issues:</CardText>
                            <CardText>Unit tests:</CardText>
                            </CardBody>
                        </Card>
                        
                    </CardDeck>
                </Col>
              </Row>
    
     </div>
      );
    }
}

export default About;
