import React, {Component} from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {RepresentativeInstance} from './RepresentativeInstance.js';
import {
    Col,
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button, CardDeck,
     } from 'reactstrap';
import {Link, BrowserRouter, Route} from 'react-router-dom';
export class Representatives extends Component {
    constructor(props) {
        super(props);
        
        
        
    }
    componentWillMount() {
        fetch('http://3.84.54.129/api/congressmembers')
        .then(res => res.json())
        .then((data) => {
            console.log(data);
            this.setState({ people: data});      
        })
        .catch(console.log)
    }
    
    render() {
        var person;
        var cspan_id;
        var date_of_birth;
        var district;
        var facebook_account;
        var first_name;
        var last_name;
        var member_id;
        var party;
        var state;
        var title;
        var twitter;
        var url;
        var votes_with_party_pct;

        var list;
        
        console.log(this.state);
        // if (this.state!= null){
        //     for(person of this.state.people.objects){
        //         console.log(person);
        //     }
        // }

        const {items} = this.state;
        if (this.state == null){
            console.log("RETURNED FALSE")
            return false;
        }
        
        
        console.log(items)
        
        
      return (
        
        <div className="Representatives" style={{width:"100%", height:"60%"}}>
        
           <div>
            <Col sm={{ size: 'auto'}}>
                {/* <CardImg top width="80%" src={john} alt="Card image cap" /> */}

                <CardDeck>
                    {items.map(item =>
                        <Card>
                            <CardBody>
                            <CardTitle>{item.objects.first_name}{item.objects.last_name}</CardTitle>
                            <CardSubtitle>State: {item.objects.state}</CardSubtitle>
                            <CardSubtitle>Party: {item.objects.party}</CardSubtitle>
                            <CardSubtitle>Years Active: 2002-present</CardSubtitle>
                            <CardSubtitle>Title:  {item.objects.title}</CardSubtitle>
                            <BrowserRouter>
                                <Button href= {"/politician/" + item.objects.member_id} class="nav-link"  to={"/politician/" + item.objects.member_id}> See More</Button>
                            </BrowserRouter>
                            </CardBody>
                        </Card>
                    )}
                    </CardDeck>
                </Col>
               
            </div>
           
        </div>
    
      );
    }
}

export default Representatives;
