import React, {Component} from "react";
import './About.css';
import 'bootstrap/dist/css/bootstrap.min.css';

import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    UncontrolledDropdown,
    DropdownToggle,  Row, Col,
    Card, CardImg, CardText, CardBody,
    CardTitle, CardSubtitle, Button, CardDeck,
    DropdownMenu,
    DropdownItem } from 'reactstrap';
import brianaImg from './briana.jpg';
import edwinImg from './edwin.jpeg';
import ryanImg from './ryan.jpg';
import ojasImg from './ojas.jpg';
import travisImg from './travis.jpeg';
export class About extends Component {
    constructor(props) {
      super(props);
      this.state = {
        people: [],
      };
    }
    componentDidMount() {
        fetch('https://gitlab.com/api/v4/projects/14474532/repository/contributors')
        .then(res => res.json())
        .then((data) => {
            console.log(data);
          this.setState({ people: data });
        })
        .catch(console.log)

        fetch('https://gitlab.com/api/v4/projects/14474532/issues')
        .then(res => res.json())
        .then((info) => {
            console.log(info);
          this.setState({ issues: info });
        })
    }
    render() {
        var briana = {'commits':0, 'issues':0}
        var ojas = {'commits':0, 'issues':0}
        var travis = {'commits':0, 'issues':0}
        var edwin = {'commits':0, 'issues':0}
        var ryan = {'commits':0, 'issues':0}
        var person;
        for(person of this.state.people){
            if(person.name == "Ojas Patel"){
                ojas['commits'] += person.commits;
            } 
            else if(person.name == "Travarin"){
                travis['commits'] += person.commits;
            }
            else if(person.name == "Edwin_Silerio"){
                edwin['commits'] += person.commits;
            }
            else if(person.name == "Ryan Morris"){
                ryan['commits'] += person.commits;
            }
            else if(person.name == "brianawhite"){
                briana['commits'] += person.commits;
            }
            
        }
        var issue;
        for(issue in this.state.issues){
            if(this.state.issues[issue].closed_by != null){
                if(this.state.issues[issue].closed_by.name == "Ojas Patel"){
                    ojas['issues'] += 1;
                } 
                else if(this.state.issues[issue].closed_by.name == "Travis Langston"){
                    travis['issues'] += 1;
                }
                else if(this.state.issues[issue].closed_by.name == "Edwin Silerio"){
                    edwin['issues'] += 1;
                }
                else if(this.state.issues[issue].closed_by.name == "Ryan Morris"){
                    ryan['issues'] += 1;
                } 
                else if(this.state.issues[issue].closed_by.name == "Briana White"){
                    briana['issues'] += 1;
                }
            }
        }
      console.log(briana)
      return (
        <div className = "main">
            <div className="About">
                <h1> About Us</h1>
                <h wrap style= {{ textAlign: "center", paddingLeft:'5%', color: '#000000'}}>We believe that everyone needs to know where their energy is coming from and who is making those decisions. Today, more than ever, we need to make the right choices and how to get our energy and what the impact of energy-related legislation is. This site contains information on states, members of Congress, and legislation related to energy so EVERYONE can make educated decisions.</h>        
                <h wrap style= {{ textAlign: "center", paddingLeft:'5%', color: '#000000'}}>Our goal in integrating the data is to see if there is a connection between different aspects of a state such as the primary partisanship, population, GDP, and energy consumption and the representatives they elect. In turn, we want to explore if there are characteristics of representatives that might determine how they vote on different energy bills and legislation. 
</h>        

            </div>
            <div className = "cards" style= {{MarginTop: 10, paddingTop: '20%'}}>
                <Row>
                    <Col sm={{ size: '10'}} style={{paddingLeft: '15%'}}>
                        <CardDeck>
                            <Card>
                                <CardImg top width="100%" src={brianaImg} />
                                <CardBody>
                                <CardTitle>Briana White</CardTitle>
                                <CardText>Bio</CardText>
                                <CardText>Frontend</CardText>
                                <CardText>Commits:{briana['commits']}</CardText>
                                <CardText>Issues:{briana['issues']}</CardText>
                                <CardText>Unit tests:0</CardText>
                                </CardBody>
                            </Card>
                            <Card>
                                <CardImg top width="100%" src={edwinImg} alt="Card image cap" />
                                <CardBody>
                                <CardTitle>Edwin Silerio</CardTitle>
                                <CardText>Bio</CardText>
                                <CardText>Frontend</CardText>
                                <CardText>Commits:{edwin['commits']}</CardText>
                                <CardText>Issues:{edwin['issues']}</CardText>
                                <CardText>Unit tests:0</CardText>
                                </CardBody>
                            </Card>
                            <Card>
                                <CardImg top width="100%" src={ojasImg} alt="Card image cap" />
                                <CardBody>
                                <CardTitle>Ojas Patel</CardTitle>
                                <CardText>Likes eating expensive pasta and watching the Teenage Mutant Ninja Turtles</CardText>
                                <CardText>Backend</CardText>
                                <CardText>Commits:{ojas['commits']}</CardText>
                                <CardText>Issues:{ojas['issues']}</CardText>
                                <CardText>Unit tests:0</CardText>
                                </CardBody>
                            </Card>
                            </CardDeck>
                        </Col>
                </Row>
                <Row style={{paddingTop: "100px", paddingLeft: "15%"}} sm={{ size: '4'}}>
                    <Col sm={{ size: '8'}}>
                        <CardDeck>
                            <Card>
                                <CardImg top width="70%" src={ryanImg} alt="Card image cap" />
                                <CardBody>
                                <CardTitle>Ryan Morris</CardTitle>
                                <CardText>Bio</CardText>
                                <CardText>Backend</CardText>
                                <CardText>Commits:{ryan['commits']}</CardText>
                                <CardText>Issues:{ryan['issues']}</CardText>
                                <CardText>Unit tests:0</CardText>
                                </CardBody>
                            </Card>
                            <Card>
                                <CardImg top width="70%" src={travisImg} alt="Card image cap" />
                                <CardBody>
                                <CardTitle>Travis Langston</CardTitle>
                                <CardText>Bio</CardText>
                                <CardText>Backend</CardText>
                                <CardText>Commits:{travis['commits']}</CardText>
                                <CardText>Issues:{travis['issues']}</CardText>
                                <CardText>Unit tests:0</CardText>
                                </CardBody>
                            </Card>
                        </CardDeck>
                    </Col>
                </Row>
            </div>
            
        </div>
      );
    }
}

export default About;
