import React, {Component} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';

import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand, ListGroup, ListGroupItem, ListGroupItemHeading, ListGroupItemText,
    Nav,
    NavItem,
    NavLink,
    UncontrolledDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem } from 'reactstrap';
import {Link} from 'react-router-dom';

export class StatesInstance extends Component {
  constructor(props) {
    super(props);
    this.state = {
        items: [],
        values: [],
        isLoading: false,
        twitter: "",
        party: "",
        img:"",
    }
    
    
}
toLowerCase(value){
    if(value != null){
      return value.toLowerCase();  
    }
}
componentDidMount() {
    var value = window.location.href.split("/")
    var path = (value[4])
    console.log('http://3.84.54.129:5000/api/states/' + path)
    this.setState({isLoading: true});
    fetch('http://3.84.54.129:5000/api/states/' + path)
    .then(res => res.json())
    .then((data) => {
        this.setState({ items: data});      
    })
    .catch(console.log)

    // fetch('http://3.84.54.129:5000/api/bills?q={"filters":[{"name":"sponsor_id","op":"eq","val":"'+ path + '"}]}')
    // .then(res => res.json())
    // .then((data) => {
    //     this.setState({ values: data.objects,isLoading: false});      
    // })
    // .catch(console.log)
}

render() {

    const {items, isLoading, values} = this.state;
    var stateMap = {
      "AL": "Alabama",
      "AK": "Alaska",
      "AZ": "Arizona",
      "AR": "Arkansas",
      "CA": "California",
      "CO": "Colorado",
      "CT": "Connecticut",
      "DE": "Delaware",
      "FL": "Florida",
      "GA": "Georgia",
      "HI": "Hawaii",
      "ID": "Idaho",
      "IL": "Illinois",
      "IN": "Indiana",
      "IA": "Iowa",
      "KS": "Kansas",
      "KY": "Kentucky",
      "LA": "Louisiana",
      "ME": "Maine",
      "MD": "Maryland",
      "MA": "Massachusetts",
      "MI": "Michigan",
      "MN": "Minnesota",
      "MS": "Mississippi",
      "MO": "Missouri",
      "MT": "Montana",
      "NE": "Nebraska",
      "NV": "Nevada",
      "NH": "New Hampshire",
      "NJ": "New Jersey",
      "NM": "New Mexico",
      "NY": "New York",
      "NC": "North Carolina",
      "ND": "North Dakota",
      "OH": "Ohio",
      "OK": "Oklahoma",
      "OR": "Oregon",
      "PA": "Pennsylvania",
      "RI": "Rhode Island",
      "SC": "South Carolina",
      "SD": "South Dakota",
      "TN": "Tennessee",
      "TX": "Texas",
      "UT": "Utah",
      "VT": "Vermont",
      "VA": "Virginia",
      "WA": "Washington",
      "WV": "West Virginia",
      "WI": "Wisconsin",
      "WY": "Wyoming",
    };
    if (isLoading){
        return false;
    }
    

    console.log(values)
    console.log(values.bill_id)        
    
      return (
        <div center className="StatesInstance"style = {{paddingLeft:'3%',textAlign: 'center',color: '#000000'}}>
            <img top width="80%" style={{width: '300px', height: '375px'}} src={"https://www.state-maps.org/"+ stateMap[items.name]+".gif"} alt={"No image"} />
            <h2 style = {{paddingTop: "3%",color: '#000000'}}> {items.name} </h2>
            <center>
            <ListGroup style = {{paddingTop:'1%', paddingLeft:'3%',textAlign: 'center',color: '#000000', width: '50%'}}>
            {/* <ListGroupItem>
                <ListGroupItemHeading>Summary</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.state.bio}
                    </ListGroupItemText>
                </ListGroupItem> */}
                <ListGroupItem>
                <ListGroupItemHeading>State</ListGroupItemHeading>
                    <ListGroupItemText>
                    <a href = {'/state/'+stateMap[(items.state)]}>{stateMap[items.state]}</a>
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                  <ListGroupItemHeading>District</ListGroupItemHeading>
                  <ListGroupItemText>
                  {items.district}
                  </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Party</ListGroupItemHeading>
                  <ListGroupItemText>
                  {this.state.party}
                  </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Date Of Birth</ListGroupItemHeading>
                    <ListGroupItemText>
                    {items.date_of_birth}
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Title</ListGroupItemHeading>
                    <ListGroupItemText>
                    {items.title}
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading> Energy Bills</ListGroupItemHeading>
                  {values.map(value =>
                    <ListGroupItemText>
                        <a href={"/bill/" + value.bill_id}>{value.bill_id}</a>
                    </ListGroupItemText>
                  )}
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Percent Of Votes With Party</ListGroupItemHeading>
                  <ListGroupItemText>
                  {items.votes_with_party_pct}
                  </ListGroupItemText>
                </ListGroupItem>
        </ListGroup>
        </center>
        </div>
      );
    }
}

export default StatesInstance;
