import React, {Component} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';

import {
    ListGroup, ListGroupItem, ListGroupItemHeading, ListGroupItemText
    } from 'reactstrap';

export class StatesInstance extends Component {
  constructor(props) {
    super(props);
    this.state = {
        items: [],
        isLoading: false,
        values: [],
        bill: [],
        bills: [],
        map: {
          "Alabama":"al",
          "Alaska":"ak",
          "Arizona":"az",
          "Arkansas":"ar",
          "California":"ca",
          "Colorado":"co",
          "Connecticut":"ct",
          "Delaware":"de",
          "Florida":"fl",
          "Georgia":"ga",
