          "Missouri":"mo",
          "Montana":"mt",
          "Nebraska":"ne",
          "Nevada":"nv",
          "New Hampshire":"nh",
          "New Jersey":"nj",
          "New Mexico":"nm",
          "New York":"ny",
          "North Carolina":"nc",
