import React, {Component} from "react";
import PropTypes from 'prop-types';
import {
    Pagination, PaginationItem, PaginationLink
    } from 'reactstrap';

    export class MyPagination extends Component {
        constructor(props) {
            super(props);
            this.state = {
                page: 1,
                nextpage: 2,
                lastpage: 1,
                isLoading: false,
            }
            
        }
        /*
        * Updates page variables that are used in pagination when the user clicks 
        * on a new page number to go to. Then calls componentDidMount() so that the 
        * new data is loaded. 
        */
        handlePageClick(newPage){
            if(newPage === 1){
                this.setState({lastpage: newPage, page: newPage, nextpage: 2}, () => {
                    this.props.changePage(this.state.page);
                });
            }
            else if(newPage === this.props.pages){
                this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage}, () => {
                    this.props.changePage(this.state.page);
                });
            }
            else{
                this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage + 1}, () => {
                    this.props.changePage(this.state.page);
                });
            }
        }
        render(){
            return(
                <div>
                    <Pagination size="lg" style={{justifyContent: 'center', paddingTop:'3%'}} >
                        <PaginationItem>
                            <PaginationLink first onClick={() =>this.props.changePage(1)} href={"#"} />
                            </PaginationItem>
                        <PaginationItem>
                            <PaginationLink previous onClick={() => this.props.changePage(this.props.lastpage)} href={"#"} />
                        </PaginationItem>
                        <PaginationItem>
                            <PaginationLink onClick={() => this.props.handlePageClick(this.props.page)} href={"#"}>
                                {this.props.page}
                            </PaginationLink>
                        </PaginationItem>
                        <PaginationItem>
                            <PaginationLink onClick={() => this.props.handlePageClick(this.props.nextpage)} href={"#"}>
                                {this.props.nextpage}
                            </PaginationLink>
                        </PaginationItem>
                        <PaginationItem>
                            <PaginationLink next onClick={() => this.props.handlePageClick(this.props.nextpage)} href={"#"} />
                        </PaginationItem>
                        <PaginationItem>
                            <PaginationLink last onClick={() => this.props.handlePageClick(this.props.pages)} href="#" />
                        </PaginationItem>
                    </Pagination>
                </div>
            );
        }
        
    }  
  
  export default MyPagination;
