import React, {Component} from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import ted from './ted.jpg';
import jeff from './small_merkley_photo.jpg';
import aoc from './aoc.jpg';
import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    UncontrolledDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem } from 'reactstrap';
import {Link} from 'react-router-dom';

export class RepresentativeInstance extends Component {
    constructor(props) {
      super(props);

    }

    render() {
      var image;
      if(this.props.name == "Ted Cruz"){
        image = {ted};
      }
      if(this.props.name == "Jeff Merkley"){
        image = {jeff};
      }
      else{
          image = {aoc};
      }
      return (
        <div className="RepresentativeInstance">
            <img src= {ted}/>
            <p style = {{color: '#000000'}}> {this.props.name} </p>
            <p style = {{color: '#000000'}}> State:{this.props.state} </p>
            <p style = {{color: '#000000'}}> Party:{this.props.party} </p>
            <p style = {{color: '#000000'}}> Years Active:{this.props.date} </p>
            <p style = {{color: '#000000'}}> Chamber:{this.props.chamber} </p>
        </div>
      );
    }
}

export default RepresentativeInstance;
