import React, {Component} from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Table,Col,
     } from 'reactstrap';
export class EnergyBills extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            page: 1,
            pages: 0,
            nextpage: 2,
            lastpage: 1,
            isLoading: false,
        }
        
        
    }
    componentDidMount() {
        this.setState({isLoading: true});
        fetch('http://3.84.54.129:5000/api/bills'+ "?page=" + this.state.page)
        .then(res => res.json())
        .then((data) => {
            this.setState({ items: data.objects,isLoading: false,pages:data.total_pages});      
        })
        .catch(console.log)
    }
    log(val){
        console.log(val)
    }
    handlePageClick(newPage){
        if(newPage === 1){
            this.setState({lastpage: newPage});
            this.setState({page: newPage});
            this.setState({nextpage: 2});
            this.componentDidMount();
        }
        else if(newPage === this.state.pages){
            this.setState({lastpage: newPage - 1});
            this.setState({page: newPage});
            this.setState({nextpage: newPage});
            this.componentDidMount();
        }
        else if (this.state.pages >= newPage){
            this.setState({lastpage: newPage - 1});
            this.setState({page: newPage});
            this.setState({nextpage: newPage + 1});
            this.componentDidMount();
        }
        
    }
    render() {
        const {items, isLoading} = this.state;
        console.log(isLoading)
        if (isLoading){
            console.log(isLoading)
            return false;
        }
        
        console.log(items)        
        
      return (
        <div className="EnergyBills" style={{paddingTop: "2%",width:"100%", height:"60%"}}>
           <div>
            <h4>Energy Bills</h4>
            <Col sm={{ size: 'auto'}} style={{paddingTop: "2%"}}>
            <Table hover responsive>
                <thead>
                <tr>
                    <th>#</th>
                    <th>Title</th>
                    <th>Sponsor</th>
                    <th>Committees</th>
                    <th>Introduced Date</th>
                    <th>House Passage</th>
                    <th>Senate Passage</th>
                    <th>Democrat Cosponsors</th>
                    <th>Republican Cosponsors</th>
                </tr>
                </thead>
                <tbody>
                    
                {items.map(item =>

                <tr>
                    <th scope="row"></th>
                    <td> <a href= {"/bill/" + item.bill_id}   to={"/bill/" + item.bill_id}>{item.title}</a></td>
                    <td> <a href= {"/politician/" + item.sponsor_id}   to={"/politician/" + item.sponsor_id}>{item.sponsor_id}</a></td>
                    <td> {item.committees}</td>
                    <td> {item.introduced_date}</td>
                    <td> {item.house_passage}</td>
                    <td> {item.senate_passage}</td>
                    <td> {item.cosponsors_by_party_D}</td>
                    <td> {item.cosponsors_by_party_R}</td>
                </tr>
                )}
                </tbody>
            </Table>
                </Col>
            </div>
       
        </div>
      );
    }
}

export default EnergyBills;
