import React, {Component} from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {RepresentativeInstance} from './RepresentativeInstance.js';
import person from './emptyperson.png';
import {
    Col,
    Card, CardImg, CardBody,
    CardTitle, CardSubtitle, Button, CardDeck, Pagination, PaginationItem, PaginationLink
     } from 'reactstrap';
import { BrowserRouter} from 'react-router-dom';
export class Representatives extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            page: 1,
            pages: 0,
            nextpage: 2,
            lastpage: 1,
            isLoading: false,
        }
        
        
    }
    componentDidMount() {
        console.log(this.state.page)
        this.setState({isLoading: true});
        fetch('http://3.84.54.129:5000/api/congressmembers'+ "?page=" + this.state.page)
        .then(res => res.json())
        .then((data) => {
            this.setState({ items: data.objects,isLoading: false,pages:data.total_pages});      
        })
        .catch(console.log)
    }
    refetch(){
        console.log(this.state.page)
        this.setState({isLoading: true});
        fetch('http://3.84.54.129:5000/api/congressmembers'+ "?page=" + this.state.page)
        .then(res => res.json())
        .then((data) => {
            this.setState({ items: data.objects,isLoading: false,pages:data.total_pages});      
        })
        .catch(console.log)
    }
    handlePageClick(newPage){
        if(newPage === 1){
            this.setState({lastpage: newPage, page: newPage, nextpage: 2}, () => {
            this.componentDidMount();
            });
        }
        else if(newPage === this.state.pages){
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage}, () => {
                this.componentDidMount();
                });
        }
        else{
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage + 1}, () => {
                this.componentDidMount();
                });
        }
        
    }
    
    render() {
        var stateMap = {
            "AL": "Alabama",
            "AK": "Alaska",
            "AZ": "Arizona",
            "AR": "Arkansas",
            "CA": "California",
            "CO": "Colorado",
            "CT": "Connecticut",
            "DE": "Delaware",
            "FL": "Florida",
            "GA": "Georgia",
            "HI": "Hawaii",
            "ID": "Idaho",
            "IL": "Illinois",
            "IN": "Indiana",
            "IA": "Iowa",
            "KS": "Kansas",
            "KY": "Kentucky",
            "LA": "Louisiana",
            "ME": "Maine",
            "MD": "Maryland",
            "MA": "Massachusetts",
            "MI": "Michigan",
            "MN": "Minnesota",
            "MS": "Mississippi",
            "MO": "Missouri",
            "MT": "Montana",
            "NE": "Nebraska",
            "NV": "Nevada",
            "NH": "New Hampshire",
            "NJ": "New Jersey",
            "NM": "New Mexico",
            "NY": "New York",
            "NC": "North Carolina",
            "ND": "North Dakota",
            "OH": "Ohio",
            "OK": "Oklahoma",
            "OR": "Oregon",
            "PA": "Pennsylvania",
            "RI": "Rhode Island",
            "SC": "South Carolina",
            "SD": "South Dakota",
            "TN": "Tennessee",
            "TX": "Texas",
            "UT": "Utah",
            "VT": "Vermont",
            "VA": "Virginia",
            "WA": "Washington",
            "WV": "West Virginia",
            "WI": "Wisconsin",
            "WY": "Wyoming",
          };
        const {items, isLoading} = this.state;
        console.log(isLoading)
        if (isLoading){
            console.log(isLoading)
            return false;
        }
        
        console.log(items)        
        
      return (
        <BrowserRouter>

        <div className="Representatives" style={{width:"100%", height:"60%"}}>
        
           <div>

            <Col sm={{ size: 'auto'}}>
                <CardDeck>
                    {items.slice(0, 3).map(item =>
                        <Card>
                            <CardImg top width="80%" src={"https://theunitedstates.io/images/congress/original/" + item.member_id + ".jpg"} alt={"No image"} />
                            <CardBody>
                            <CardTitle>{item.first_name + " " + item.last_name}</CardTitle>
                            <CardSubtitle>State: {stateMap[item.state]}</CardSubtitle>
                            <CardSubtitle>Party: {item.party}</CardSubtitle>
                            <CardSubtitle>Title:  {item.title}</CardSubtitle>
                            <Button href= {"/politician/" + item.member_id} class="nav-link"  to={"/politician/" + item.member_id} render={(props) => <RepresentativeInstance {...props} name={item.member_id} />}> See More</Button>
                            </CardBody>
                            
                        </Card>
                    )}
                </CardDeck>
                <CardDeck>
                    {items.slice(3, 6).map(item =>
                        <Card>
                            <CardImg top width="80%" src={"https://theunitedstates.io/images/congress/original/" + item.member_id + ".jpg"} alt={"No image"} />
                            <CardBody>
                            <CardTitle>{item.first_name + " " + item.last_name}</CardTitle>
                            <CardSubtitle>State: {item.state}</CardSubtitle>
                            <CardSubtitle>Party: {item.party}</CardSubtitle>
                            <CardSubtitle>Title:  {item.title}</CardSubtitle>
                                <Button href= {"/politician/" + item.member_id} class="nav-link"  to={"/politician/" + item.member_id} render={(props) => <RepresentativeInstance {...props} name={item.member_id} />}> See More</Button>
                            </CardBody>
                            
                        </Card>
                    )}
                </CardDeck>
                <CardDeck>
                    {items.slice(6, 10).map(item =>
                        <Card>
                            <CardImg top width="80%" src={"https://theunitedstates.io/images/congress/original/" + item.member_id + ".jpg"} alt={"No image"} />
                            <CardBody>
                            <CardTitle>{item.first_name + " " + item.last_name}</CardTitle>
                            <CardSubtitle>State: {item.state}</CardSubtitle>
                            <CardSubtitle>Party: {item.party}</CardSubtitle>
                            <CardSubtitle>Title:  {item.title}</CardSubtitle>
                            <Button href= {"/politician/" + item.member_id} class="nav-link"  to={"/politician/" + item.member_id} render={(props) => <RepresentativeInstance {...props} name={item.member_id} />}> See More</Button>
                            </CardBody>
                            
                        </Card>
                    )}
                </CardDeck>

            </Col>

            </div>
            <div>
        <Pagination size="sm" aria-label="Page navigation example">
        <PaginationItem>
          <PaginationLink first onClick={() => this.handlePageClick(1)} href={"#"} />
        </PaginationItem>
        <PaginationItem>
          <PaginationLink previous onClick={() => this.handlePageClick(this.state.lastpage)} href={"#"} />
        </PaginationItem>
        {/* <PaginationItem>
          <PaginationLink onClick={() => this.handlePageClick(this.state.lastpage)} href={"#"}>
            {this.state.lastpage}
          </PaginationLink>
        </PaginationItem> */}
        <PaginationItem>
          <PaginationLink onClick={() => this.handlePageClick(this.state.page)} href={"#"}>
            {this.state.page}
          </PaginationLink>
        </PaginationItem>
        {/* <PaginationItem>
          <PaginationLink onClick={() => this.handlePageClick(this.state.nextpage)} href={"#"}>
            {this.state.nextpage}
          </PaginationLink>
        </PaginationItem> */}
        <PaginationItem>
          <PaginationLink next onClick={() => this.handlePageClick(this.state.nextpage)} href={"#"} />
        </PaginationItem>
        <PaginationItem>
          <PaginationLink last onClick={() => this.handlePageClick(this.state.pages)} href="#" />
        </PaginationItem>
      </Pagination>
      </div>
        </div>
        </BrowserRouter>

      );
    }
}

export default Representatives;
