import React, {Component,  useState} from "react";
import './Navbar.css';
//import Highlighter from "react-highlight-words";
import 'bootstrap/dist/css/bootstrap.min.css';
import classnames from 'classnames';
import { Tab, Tabs, TabList, TabPanel } from 'react-tabs';
import {
    Table,Col, TabContent, Pagination, PaginationItem, PaginationLink, Form, FormGroup, Label, Input , TabPane, Nav, NavItem, NavLink, Card, Button, CardTitle, CardText, Row
     } from 'reactstrap';

export class Search extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            page: 1,
            pages: 0,
            nextpage: 2,
            lastpage: 1,
            isLoading: false,
            bills: [],
            activeTab:[],
            terms:[],

        }
        
        
    }

        
    toggle(tab){
        if(this.state.activeTab !== tab){
            this.setState({activeTab:tab});
        }
    }



    componentDidMount(){
        var value = window.location.href.split("/")
        var path = value[4]
        if(path.includes("%20")){
            path = path.split("%20");
        }
        else{
            path = [value[4]];
        }
        console.log(path)
        console.log(path.length)
        this.setState({terms:path}); 
        var url = 'https://api.empoweringknowledge.me/api/bills?q={"filters":[{"or":[';
        url += '{"name":"title","op":"like","val":"%'+ path[0] +'%"}';
        url += ',{"name":"sponsor_id","op":"like","val":"%'+ path[0] +'%"}';
        url += ',{"name":"committees","op":"like","val":"%'+ path[0] +'%"}';
        url += ',{"name":"introduced_date","op":"like","val":"%'+ path[0] +'%"}';
        url += ',{"name":"cosponsors_by_party_R","op":"like","val":"%'+ path[0] +'%"}';
        url += ',{"name":"house_passage","op":"like","val":"%'+ path[0] +'%"}';
        url += ',{"name":"senate_passage","op":"like","val":"%'+ path[0] +'%"}';
        url += ',{"name":"cosponsors_by_party_D","op":"like","val":"%'+ path[0] +'%"}';
        for(var i = 1; i < path.length; i++){
            url += ',{"name":"title","op":"like","val":"%'+ path[i] +'%"}';
            url += ',{"name":"sponsor_id","op":"like","val":"%'+ path[i] +'%"}';
            url += ',{"name":"committees","op":"like","val":"%'+ path[i] +'%"}';
            url += ',{"name":"introduced_date","op":"like","val":"%'+ path[i] +'%"}';
            url += ',{"name":"cosponsors_by_party_R","op":"like","val":"%'+ path[i] +'%"}';
            url += ',{"name":"house_passage","op":"like","val":"%'+ path[i] +'%"}';
            url += ',{"name":"senate_passage","op":"like","val":"%'+ path[i] +'%"}';
            url += ',{"name":"cosponsors_by_party_D","op":"like","val":"%'+ path[i] +'%"}';
        }
    
        url += "]}]}";
        console.log(url);
        fetch(url)
        .then(res => res.json())
        .then((data) => {
            this.setState({bills:data.objects});  
        })
        .catch(console.log)
    }
   
    render(){
        return(
            <div>
               
            <Tabs>
                <TabList>
                <Tab>Bills</Tab>
                <Tab>Politicians</Tab>
                <Tab>States</Tab>
                </TabList>

                <TabPanel>
                <Row>
                    <Col sm="12">
                    <Table hover responsive>
                    <thead>
                    <tr>
                        <th>#</th>
                        <th>Title</th>
                        <th>Sponsor</th>
                        <th>Committees</th>
                        <th>Introduced Date</th>
                        <th>House Passage</th>
                        <th>Senate Passage</th>
                        <th>Democrat Cosponsors</th>
                        <th>Republican Cosponsors</th>
                    </tr>
                    </thead>
                    <tbody>     
                    {this.state.bills.map(item =>

                    <tr>
                        <th scope="row"></th>
                        <td> <a href= {"/bill/" + item.bill_id}   to={"/bill/" + item.bill_id}>{item.title}</a></td>
                        <td> <a href= {"/politician/" + item.sponsor_id}   to={"/politician/" + item.sponsor_id}>{item.sponsor_id}</a></td>
                        <td> {item.committees}</td>
                        <td> {item.introduced_date}</td>
                        <td> {item.house_passage}</td>
                        <td> {item.senate_passage}</td>
                        <td> {item.cosponsors_by_party_D}</td>
                        <td> {item.cosponsors_by_party_R}</td>
                    </tr>
                    )}
                    </tbody>
                    </Table>
                    </Col>
                </Row>
                </TabPanel>
                <TabPanel>
                <Row>
                    <Col sm="6">
                    <Card body>
                        <CardTitle>Special Title Treatment</CardTitle>
                        <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                        <Button>Go somewhere</Button>
                    </Card>
                    </Col>
                    <Col sm="6">
                    <Card body>
                        <CardTitle>Special Title Treatment</CardTitle>
                        <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                        <Button>Go somewhere</Button>
                    </Card>
                    </Col>
                </Row>
                </TabPanel>
                <TabPanel>
                <Row>
                    <Col sm="6">
                    <Card body>
                        <CardTitle>Special Title Treatment</CardTitle>
                        <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                        <Button>Go somewhere</Button>
                    </Card>
                    </Col>
                    <Col sm="6">
                    <Card body>
                        <CardTitle>Special Title Treatment</CardTitle>
                        <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                        <Button>Go somewhere</Button>
                    </Card>
                    </Col>
                </Row>
                </TabPanel>
            </Tabs>

            </div>
        );
    }
}
export default Search;
