import React, {Component,  useState} from "react";
import './Navbar.css';
import Highlighter from "react-highlight-words";
import 'bootstrap/dist/css/bootstrap.min.css';
import classnames from 'classnames';
import {
    Table,Col, TabContent, Pagination, PaginationItem, PaginationLink, Form, FormGroup, Label, Input , TabPane, Nav, NavItem, NavLink, Card, Button, CardTitle, CardText, Row
     } from 'reactstrap';

const Search = (props) => {
    const [state, setState] = React.useState({
        terms: [],
        politicians: [],
        bills: [],
        states: []
      });

    const [activeTab, setActiveTab] = useState('1');

    const toggle = tab => {
        if(activeTab !== tab) setActiveTab(tab);
    }



    let getBills = () =>{
        var value = window.location.href.split("/")
        var path = value[4]
        if(path.includes("%20")){
            path = path.split("%20");
        }
        console.log(path)
        var url = "https://api.empoweringknowledge.me/api/bills?q={filters:[{or:[";
        for(var i; i < path.length(); i++){
            console.log(i);
            url += '{"name":"title","op":"like","val":"%'+ path[i] +'%"},';
            url += '{"name":"sponsor","op":"like","val":"%'+ path[i] +'%"},';
            url += '{"name":"committiees","op":"like","val":"%'+ path[i] +'%"},';
            url += '{"name":"introduced_date","op":"like","val":"%'+ path[i] +'%"},';
            url += '{"name":"cosponsors_by_party_R","op":"like","val":"%'+ path[i] +'%"},';
            url += '{"name":"house_passage","op":"like","val":"%'+ path[i] +'%"},';
            url += '{"name":"senate_passage","op":"like","val":"%'+ path[i] +'%"},';
            url += '{"name":"cosponsors_by_party_D","op":"like","val":"%'+ path[i] +'%"},';
        }
    
        url += "]}]}";
        console.log(url);
        // fetch('https://dm5v6ty0dwipl.cloudfront.net/api/bills'+ "?page=" + this.state.page)
        // .then(res => res.json())
        // .then((data) => {
        //     this.setState({ items: data.objects,isLoading: false,pages:data.total_pages});      
        // })
        // .catch(console.log)
    }
    let getPoliticians = () =>{
        var value = window.location.href.split("/")
        var path = value[4]
        if(path.includes("%20")){
            path = path.split("%20");
        }

    }
    let getStates = () =>{
        var value = window.location.href.split("/")
        var path = value[4]
        if(path.includes("%20")){
            path = path.split("%20");
        }

    }

    return(
        <div>

        <Nav tabs>
            <NavItem>
            <NavLink
                className={classnames({ active: activeTab === '1' })}
                onClick={() => { toggle('1'); }}
            >
                Bills
            </NavLink>
            </NavItem>
            <NavItem>
            <NavLink
                className={classnames({ active: activeTab === '2' })}
                onClick={() => { toggle('2'); }}
            >
                Politicians
            </NavLink>
            </NavItem>
            <NavItem>
            <NavLink
                className={classnames({ active: activeTab === '3' })}
                onClick={() => { toggle('3'); }}
            >
                States
            </NavLink>
            </NavItem>
        </Nav>



        <TabContent activeTab={activeTab}>
            <TabPane tabId="1">
            <Row>
                <Col sm="12">
                <Table hover responsive>
                <thead>
                <tr>
                    <th>#</th>
                    <th>Title</th>
                    <th>Sponsor</th>
                    <th>Committees</th>
                    <th>Introduced Date</th>
                    <th>House Passage</th>
                    <th>Senate Passage</th>
                    <th>Democrat Cosponsors</th>
                    <th>Republican Cosponsors</th>
                </tr>
                </thead>
                <tbody>     
                {getBills()}               
                {/* {items.map(item =>

                <tr>
                    <th scope="row"></th>
                    <td> <a href= {"/bill/" + item.bill_id}   to={"/bill/" + item.bill_id}>{item.title}</a></td>
                    <td> <a href= {"/politician/" + item.sponsor_id}   to={"/politician/" + item.sponsor_id}>{item.sponsor_id}</a></td>
                    <td> {item.committees}</td>
                    <td> {item.introduced_date}</td>
                    <td> {item.house_passage}</td>
                    <td> {item.senate_passage}</td>
                    <td> {item.cosponsors_by_party_D}</td>
                    <td> {item.cosponsors_by_party_R}</td>
                </tr>
                )} */}
                </tbody>
                </Table>
                </Col>
            </Row>
            </TabPane>



            <TabPane tabId="2">
            <Row>
                <Col sm="6">
                <Card body>
                    <CardTitle>Special Title Treatment</CardTitle>
                    <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                    <Button>Go somewhere</Button>
                </Card>
                </Col>
                <Col sm="6">
                <Card body>
                    <CardTitle>Special Title Treatment</CardTitle>
                    <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                    <Button>Go somewhere</Button>
                </Card>
                </Col>
            </Row>
            </TabPane>



            <TabPane tabId="3">
            <Row>
                <Col sm="6">
                <Card body>
                    <CardTitle>Special Title Treatment</CardTitle>
                    <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                    <Button>Go somewhere</Button>
                </Card>
                </Col>
                <Col sm="6">
                <Card body>
                    <CardTitle>Special Title Treatment</CardTitle>
                    <CardText>With supporting text below as a natural lead-in to additional content.</CardText>
                    <Button>Go somewhere</Button>
                </Card>
                </Col>
            </Row>
            </TabPane>
        </TabContent>
        </div>
    );
    }
export default Search;
