import React, {Component} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import { Timeline } from 'react-twitter-widgets'

import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    UncontrolledDropdown, ListGroup, ListGroupItem, ListGroupItemHeading, ListGroupItemText,
    DropdownToggle,
    DropdownMenu,
    DropdownItem } from 'reactstrap';

export class RepresentativeInstance extends Component {
  constructor(props) {
    super(props);
    this.state = {
        items: [],
        isLoading: false,
    }
    
    
}
componentDidMount() {
    var value = window.location.href.split("/")
    var path = (value[4])
    this.setState({isLoading: true});
    fetch('http://3.84.54.129:5000/api/congressmembers/' + path)
    .then(res => res.json())
    .then((data) => {
        this.setState({ items: data,isLoading: false});      
    })
    .catch(console.log)
}

render() {

    const {items, isLoading} = this.state;
    console.log(isLoading)
    if (isLoading){
        console.log(isLoading)
        return false;
    }
    
    console.log(items)        
    
      return (
        
        <div center className="RepresentativeInstance"style = {{paddingLeft:'3%',textAlign: 'center',color: '#000000'}}>
            <img style={{paddingTop:'4%',width: '260px', height: '375px'}} src={"https://theunitedstates.io/images/congress/original/" + item.member_id + ".jpg"}/>
            <h2 style = {{paddingTop: "3%",color: '#000000'}}> {items.first_name} {items.last_name} </h2>
            <center>
            <ListGroup style = {{paddingTop:'1%', paddingLeft:'3%',textAlign: 'center',color: '#000000', width: '50%'}}>
            <ListGroupItem>
                <ListGroupItemHeading>Summary</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.state.bio}
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>State</ListGroupItemHeading>
                    
                    <ListGroupItemText>
                    <a href = {state}>{this.props.state}</a>
                    </ListGroupItemText>
                    </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Party</ListGroupItemHeading>
                <ListGroupItemText>
                {this.props.party}
                </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Years Active</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.props.date}
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Chamber</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.props.chamber}
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Bills</ListGroupItemHeading>
                    <ListGroupItemText>
                        <a href={bill}>{billname}</a>
                    </ListGroupItemText>
                </ListGroupItem>
        </ListGroup>
        </center>
            <div>
                <Timeline
                dataSource={{
                sourceType: 'profile',
                screenName: twitter
                }}
                options={{
                height: '400'
            }}/>
        </div>
        </div>
        

      );
    }
}

export default RepresentativeInstance;
