import React, {Component} from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import ted from './ted.jpg';
import jeff from './small_merkley_photo.jpg';
import aoc from './aoc.jpg';
import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    UncontrolledDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem } from 'reactstrap';
import {Link} from 'react-router-dom';

export class RepresentativeInstance extends Component {
    constructor(props) {
      super(props);
      this.state = {
        image: {ted},
        bio:""
      };
      img.src = ted
    }

    render() {
     console.log(this.props.name)
      if(this.props.name == "Ted Cruz"){
        this.state.image = "ted";
        this.state.bio = "Rafael Edward Cruz (born December 22, 1970) is an American politician and attorney serving as the junior United States Senator for Texas since 2013. He was the runner-up for the Republican nomination for President of the United States in the 2016 election."
      }
      else if(this.props.name == "Jeff Merkley"){
        this.state.image = "small_markley_photo";
        this.state.bio = "Jeffrey Alan Merkley (born October 24, 1956) is an American politician serving as the junior United States Senator from Oregon since 2009. He is a member of the Democratic Party. Prior to his election to the Senate, Merkley was a five-term member of the Oregon House of Representatives representing the state's 47th district, in central Multnomah County at the eastern side of Portland. From 2007 to 2009 he was Speaker of the Oregon House of Representatives."
      }
      else{
        this.state.image = "aoc";
        this.state.bio = "Alexandria Ocasio-Cortez (born October 13, 1989), often referred to by her initials, AOC, is an American politician and activist who serves as the U.S. Representative for New York's 14th congressional district. The district includes the eastern part of the Bronx and portions of north-central Queens in New York City. She is a member of the Democratic Party."
      }
      return (
        <div center className="RepresentativeInstance"style = {{paddingLeft:'3%',textAlign: 'center',color: '#000000'}}>
            <img style={{paddingTop:'4%',width: '260px', height: '375px'}} src='/ted.jpg'/>
            <p style = {{paddingTop: "3%",color: '#000000'}}> {this.props.name} </p>
            <p style = {{color: '#000000'}}> {this.state.bio} </p>
            <p style = {{color: '#000000'}}> State:{this.props.state} </p>
            <p style = {{color: '#000000'}}> Party:{this.props.party} </p>
            <p style = {{color: '#000000'}}> Years Active:{this.props.date} </p>
            <p style = {{color: '#000000'}}> Chamber:{this.props.chamber} </p>
        </div>
      );
    }
}

export default RepresentativeInstance;
