import React, {Component} from "react";
import './Navbar.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import {
    Table,Col, Pagination, PaginationItem, PaginationLink, Form, FormGroup, Label, Input, Button
     } from 'reactstrap';
export class EnergyBills extends Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [],
            page: 1,
            pages: 0,
            nextpage: 2,
            lastpage: 1,
            isLoading: false,
            input: "",
        }
        this.handleTermChange = this.handleTermChange.bind(this);
        this.handleEnter = this.handleEnter.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.handleSearch = this.handleSearch.bind(this);
    }
    componentDidMount() {
        this.setState({isLoading: true});
        var val;
        fetch('https://dm5v6ty0dwipl.cloudfront.net/api/bills'+ "?page=" + this.state.page)
        .then(res => res.json())
        .then((data) => {
            val = data.objects
            this.setState({ items: data.objects,isLoading: false,pages:data.total_pages});      
        })
        .catch(console.log)
