              }
          }
        }
    }
 
    handlePageClick(newPage){
        if(newPage === 1){
            this.setState({lastpage: newPage, page: newPage, nextpage: 2}, () => {
            this.componentDidMount();
            });
        }
        else if(newPage === this.state.pages){
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage}, () => {
                this.componentDidMount();
                });
        }
        else{
            this.setState({lastpage: newPage - 1, page: newPage, nextpage: newPage + 1}, () => {
                this.componentDidMount();
                });
        }
        
    }

    
    

    handleTermChange(e) {
        this.setState({ input: e.target.value });
    }

    handleSearch() {
        let searchTerms = [...new Set(this.state.input.toLowerCase().trim().split(" "))];
        this.state.items.map(item =>
          console.log(item)        
          
        )
        console.log(this.state.input)

    }

    handleEnter(e) {
        if (e.key === 13) {
        this.handleSearch();
        }
    }

    handleSubmit(e) {
        e.preventDefault();
        this.handleSearch();
   }

    render() {
        const {items, isLoading} = this.state;
        //console.log(isLoading)
        if (isLoading){
            //console.log(isLoading)
            return false;
        }
        
       // console.log(items)     
        const { input } = this.state.input;   
        
      return (
        <div className="EnergyBills" style={{paddingTop: "2%",width:"100%", height:"60%"}}>
           <div>
           <Form className="searchbox" onSubmit={this.handleSubmit}>
            <FormGroup style={{width: '50%'}}>
                <Label for="exampleEmail">Search</Label>
                <Input  /*innerRef={(node) => this.state.input = node}*/
                        value= {input}
                        onChange={this.handleTermChange}
                        onKeyDown={this.handleEnter}
                />
                <Button onClick={ (e) => {
                          this.handleSearch(e)
                        } }>Search</Button>
              </FormGroup>
            </Form>
            <h4>Energy Bills</h4>
            <Col sm={{ size: 'auto'}} style={{paddingTop: "2%"}}>
            <Table hover responsive>
                <thead>
                <tr>
                    <th>#</th>
                    <th>Title</th>
                    <th>Sponsor</th>
                    <th>Committees</th>
                    <th>Introduced Date</th>
                    <th>House Passage</th>
                    <th>Senate Passage</th>
                    <th>Democrat Cosponsors</th>
                    <th>Republican Cosponsors</th>
                </tr>
                </thead>
                <tbody>
                    
                {items.map(item =>
                  
                    <tr>
                        <th scope="row"></th>
                        <td> <a href= {"/bill/" + item.bill_id}   to={"/bill/" + item.bill_id}>{item.title}</a></td>
                        <td> <a href= {"/politician/" + item.sponsor_id}   to={"/politician/" + item.sponsor_id}>{item.sponsor_id}</a></td>
                        <td> {item.committees}</td>
                        <td> {item.introduced_date}</td>
                        <td> {item.house_passage}</td>
                        <td> {item.senate_passage}</td>
                        <td> {item.cosponsors_by_party_D}</td>
                        <td> {item.cosponsors_by_party_R}</td>
                    </tr>
                )}
                </tbody>
            </Table>
                </Col>
            </div>
        <div>
        <Pagination size="lg" style={{justifyContent: 'center', paddingTop:'3%'}} aria-label="Page navigation example">
        <PaginationItem>
          <PaginationLink first onClick={() => this.handlePageClick(1)} href={"#"} />
        </PaginationItem>
        <PaginationItem>
          <PaginationLink previous onClick={() => this.handlePageClick(this.state.lastpage)} href={"#"} />
        </PaginationItem>
        {/* <PaginationItem>
          <PaginationLink onClick={() => this.handlePageClick(this.state.lastpage)} href={"#"}>
            {this.state.lastpage}
          </PaginationLink>
        </PaginationItem> */}
        <PaginationItem>
          <PaginationLink onClick={() => this.handlePageClick(this.state.page)} href={"#"}>
            {this.state.page}
          </PaginationLink>
        </PaginationItem>
        <PaginationItem>
          <PaginationLink onClick={() => this.handlePageClick(this.state.nextpage)} href={"#"}>
            {this.state.nextpage}
          </PaginationLink>
        </PaginationItem>
        <PaginationItem>
          <PaginationLink next onClick={() => this.handlePageClick(this.state.nextpage)} href={"#"} />
        </PaginationItem>
        <PaginationItem>
          <PaginationLink last onClick={() => this.handlePageClick(this.state.pages)} href="#" />
        </PaginationItem>
      </Pagination>
      </div>
        </div>
      );
    }
}

export default EnergyBills;
