import React, {Component} from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import texas from './texas.png';
import california from './california.png';
import newYork from './new_York.png';
import {
    Collapse,
    Navbar,
    NavbarToggler,
    NavbarBrand,
    Nav,
    NavItem,
    NavLink,
    UncontrolledDropdown,
    DropdownToggle,
    DropdownMenu,
    DropdownItem } from 'reactstrap';
import {Link} from 'react-router-dom';

export class StatesInstance extends Component {
    constructor(props) {
      super(props);
      this.state = {
        bio:"",
    
      };
    }

    render() {
        var image;
     console.log(this.props.name)
      if(this.props.name == "California"){
        image = "california";
        this.state.bio = ""
      }
      else if(this.props.name == "New York"){
        image = "new_York";
        this.state.bio = ""
      }
      else{
        image = "texas";
        this.state.bio = ""
      }
      return (
        <div center className="StatesInstance"style = {{paddingLeft:'3%',textAlign: 'center',color: '#000000'}}>
            <img style={{paddingTop:'4%',width: '260px', height: '375px'}} src={require('./'+image+'.png')}/>
            <p style = {{paddingTop: "3%",color: '#000000', fontSize:'25px'}}> {this.props.name} </p>
            <p style = {{color: '#000000'}}> Major Energy Sources:{this.state.bio} </p>
            <p style = {{color: '#000000'}}> Population:{this.props.population} </p>
            <p style = {{color: '#000000'}}> GDP:{this.props.gdp} </p>
            <p style = {{color: '#000000'}}> Region:{this.props.region} </p>
            <p style = {{color: '#000000'}}> Dominant Political Party:{this.props.party} </p>

            <center>
            <ListGroup style = {{paddingTop:'3%', paddingLeft:'3%',textAlign: 'center',color: '#000000', width: '50%'}}>
            <ListGroupItem>
                <ListGroupItemHeading>Major Energy Source</ListGroupItemHeading>
                    <ListGroupItemText>
                    source
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Population</ListGroupItemHeading>
                    <ListGroupItemText>
                    <img style={{paddingRight:'2%',width: '90px', height: '100px'}} src={require('./'+image+'.jpeg')}/>
                    </ListGroupItemText>
                    <ListGroupItemText>
                    {this.props.population}
                    </ListGroupItemText>
                    </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>GDP</ListGroupItemHeading>
                <ListGroupItemText>
                {this.props.gdp}
                </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Region</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.props.region}
                    </ListGroupItemText>
                </ListGroupItem>
                <ListGroupItem>
                <ListGroupItemHeading>Dominant Political Party</ListGroupItemHeading>
                    <ListGroupItemText>
                    {this.props.party}
                    </ListGroupItemText>
                </ListGroupItem>
        </ListGroup>
        </center>
        </div>
      );
    }
}

export default StatesInstance;
